  typia.assert(createdTenant);
  TestValidator.equals("archived is false on creation")(createdTenant.archived)(false);

  // 2. Archive the tenant (DELETE = set archived=true)
  const archivedTenant: IAiMallBackendTenants = await api.functional.aiMallBackend.tenants.erase(connection, { id: createdTenant.id });
  typia.assert(archivedTenant);
  TestValidator.equals("archived must be true")(archivedTenant.archived)(true);
  TestValidator.equals("id must remain the same")(archivedTenant.id)(createdTenant.id);
  TestValidator.equals("code must remain the same")(archivedTenant.code)(createdTenant.code);
  TestValidator.equals("name must remain the same")(archivedTenant.name)(createdTenant.name);
  TestValidator.equals("created_at must remain the same")(archivedTenant.created_at)(createdTenant.created_at);
  // updated_at should be >= original updated_at
  TestValidator.predicate("updated_at should be updated or equal")(
    new Date(archivedTenant.updated_at).getTime() >= new Date(createdTenant.updated_at).getTime(),
  );
}

