import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";
import type { IPageIAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendSections";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validates that soft-deleted sections are excluded from section search By Default.
 *
 * Ensures platform correctly omits sections with deleted_at set from regular queries unless `include_deleted` is explicitly requested. The scenario covers:
 *
 * 1. Create a new channel (prerequisite for any section).
 * 2. Register multiple sections under the channel.
 * 3. Soft-delete (set deleted_at) for one or more of those sections.
 * 4. Perform a search for all sections on the channel WITHOUT include_deleted: expecting only active, not soft-deleted sections.
 * 5. Assert omitted (deleted) sections are not returned in results while live sections are present, confirming that soft-delete logic is enforced by default in index/search API.
 */
export async function test_api_aiMallBackend_test_search_sections_excludes_soft_deleted_by_default(
  connection: api.IConnection,
) {
  // 1. Create a new channel
  const channelBody = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(),
    code: RandomGenerator.alphaNumeric(10),
    name: RandomGenerator.paragraph()(2),
    enabled: true,
  } satisfies IAiMallBackendChannels.ICreate;
  const channel = await api.functional.aiMallBackend.channels.create(connection, { body: channelBody });
  typia.assert(channel);

  // 2. Register several sections under THAT channel
  const sections = await ArrayUtil.asyncRepeat(5)(async (i) => {
    const body = {
      channel_id: channel.id,
      code: RandomGenerator.alphaNumeric(8) + i,
      name: RandomGenerator.paragraph()(2) + i,
      enabled: true,
    } satisfies IAiMallBackendSections.ICreate;
    const section = await api.functional.aiMallBackend.sections.create(connection, { body });
    typia.assert(section);
    return section;
  });

  // 3. Soft-delete (erase) the first two sections
  const deletedSections = await Promise.all([
    api.functional.aiMallBackend.sections.erase(connection, { id: sections[0].id }),
    api.functional.aiMallBackend.sections.erase(connection, { id: sections[1].id })
  ]);
  deletedSections.forEach(s => typia.assert(s));

  // 4. Search sections (should EXCLUDE soft-deleted by default)
  const searchBody = {
    channel_id: channel.id,
    include_deleted: false
  } satisfies IAiMallBackendSections.IRequest;
  const searchResult = await api.functional.aiMallBackend.sections.index(connection, { body: searchBody });
  typia.assert(searchResult);

  // 5. Assert none of the soft-deleted sections are in the result set
  const returnedIds = searchResult.data.map(x => x.id);
  for (const deleted of deletedSections) {
    TestValidator.predicate("deleted section is omitted")(!returnedIds.includes(deleted.id));
  }
  // Assert all non-deleted sections are present
  for (const section of sections.slice(2)) {
    TestValidator.predicate("live section is present")(returnedIds.includes(section.id));
  }
}

