import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validate listing of tenants with default pagination and no filters.
 *
 * This test ensures the admin can retrieve a paginated list of all tenants
 * with default parameters (no code/name/archived filters or custom sorting/limits).
 *
 * Key validations:
 * - Created tenants are returned by the list endpoint
 * - Pagination metadata (limit, current page, total records/pages) is present and accurate
 * - Default ordering (by created_at, desc or asc as per system default)
 * - No filters hides tenants
 *
 * Test Steps:
 * 1. Create several tenants via the create endpoint, each with unique code and name.
 * 2. List tenants via the default index endpoint (no filters or pagination parameters)
 * 3. Assert the expected tenants are present in the returned page
 * 4. Validate pagination data for reasonableness (limit > 0, records >= created, pages >= 1)
 * 5. (Optional) Check if recent tenants are at the expected location depending on sort order
 */
export async function test_api_aiMallBackend_tenants_test_list_tenants_with_default_pagination(
  connection: api.IConnection,
) {
  // 1. Create several tenants with unique code and name
  const tenants: IAiMallBackendTenants[] = await ArrayUtil.asyncRepeat(3)(async (i) => {
    const code = `test-tenant-${RandomGenerator.alphaNumeric(5)}-${i}`;
    const name = `Test Tenant ${i} - ${RandomGenerator.alphabets(5)}`;
    const archived = i % 2 === 0; // Alternate archive state
    const created = await api.functional.aiMallBackend.tenants.create(connection, {
      body: {
        code,
        name,
        archived,
      } satisfies IAiMallBackendTenants.ICreate,
    });
    typia.assert(created);
    return created;
  });

  // 2. List tenants with default pagination/filtering (empty body)
  const result = await api.functional.aiMallBackend.tenants.index(connection, {
    body: {},
  });
  typia.assert(result);

  // 3. Assert that all test tenants appear in the first page
  for (const tenant of tenants) {
    const found = result.data.find((t) => t.id === tenant.id);
    TestValidator.predicate("tenant present in list")(!!found);
    if (found) {
      // Validate core fields equality
      TestValidator.equals("tenant code matches")(found.code)(tenant.code);
      TestValidator.equals("tenant name matches")(found.name)(tenant.name);
      TestValidator.equals("tenant archived flag matches")(found.archived)(tenant.archived);
    }
  }

  // 4. Validate pagination structure & metadata
  TestValidator.predicate("limit > 0")(result.pagination.limit > 0);
  TestValidator.predicate("records >= tenants created")(result.pagination.records >= tenants.length);
  TestValidator.predicate("pages >= 1")(result.pagination.pages >= 1);

  // 5. Optionally, check default result order (created_at desc/asc)
  // If system default is 'created_at desc', newest tenants should appear first
  if (result.data.length > 1) {
    const timestamps = result.data.map((t) => new Date(t.created_at).getTime());
    for (let i = 1; i < timestamps.length; ++i) {
      // Descending order check
      TestValidator.predicate("result set is sorted: created_at desc")(
        timestamps[i - 1] >= timestamps[i],
      );
    }
  }
}

