import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Validates not-found error handling for AI Mall Backend channel detail fetch by id,
 * including both non-existent IDs and soft-deleted records.
 *
 * This test ensures that:
 * - Fetching a channel by a random, never-created UUID returns a not-found error.
 * - Fetching a channel that has been soft-deleted (deleted_at set) also returns a not-found error.
 *
 * Steps:
 * 1. Create a new tenant (so that a valid tenant_id can be used for channel creation).
 * 2. Create a new active channel under this tenant; capture its ID.
 * 3. Attempt to retrieve a channel using a freshly random UUID (never created).
 *    - Expect a not-found error (TestValidator.error).
 * 4. Retrieve the newly created channel by id (sanity, should succeed).
 * 5. Soft-delete that channel (delete by id).
 * 6. Try to retrieve the same channel again; expect a not-found error due to soft-deletion.
 */
export async function test_api_aiMallBackend_test_get_channel_details_not_found_or_deleted(
  connection: api.IConnection,
) {
  // 1. Create a tenant for context
  const tenant = await api.functional.aiMallBackend.tenants.create(connection, {
    body: {
      code: RandomGenerator.alphaNumeric(10),
      name: RandomGenerator.name(),
      archived: false,
    } satisfies IAiMallBackendTenants.ICreate,
  });
  typia.assert(tenant);

  // 2. Create a new channel under this tenant
  const channel = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenant.id,
      code: RandomGenerator.alphaNumeric(6),
      name: RandomGenerator.paragraph()(),
      enabled: true,
    } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channel);

  // 3. Attempt fetching a channel by a random non-existent id; expect not-found
  const nonExistentId = typia.random<string & tags.Format<"uuid">>();
  TestValidator.error("fetch non-existent channel should 404")(() =>
    api.functional.aiMallBackend.channels.at(connection, { id: nonExistentId }),
  );

  // 4. Sanity: channel retrieval by its own id (should succeed)
  const fetched = await api.functional.aiMallBackend.channels.at(connection, { id: channel.id });
  typia.assert(fetched);
  TestValidator.equals("channel.id")(fetched.id)(channel.id);

  // 5. Soft-delete the channel
  await api.functional.aiMallBackend.channels.erase(connection, { id: channel.id });

  // 6. Attempt to fetch the soft-deleted channel; expect not-found error
  TestValidator.error("fetch soft-deleted channel should 404")(() =>
    api.functional.aiMallBackend.channels.at(connection, { id: channel.id }),
  );
}

