  TestValidator.predicate("archived tenant is found")(archivedList.data.some(t => t.id === archivedTenant.id));

  // 4. List tenants with archived=false
  const activeList = await api.functional.aiMallBackend.tenants.index(connection, {
    body: { archived: false, limit: 10, page: 1 },
  });
  typia.assert(activeList);
  TestValidator.predicate("active list includes only active tenants")(activeList.data.every(t => t.archived === false));
  TestValidator.predicate("active tenant is found")(activeList.data.some(t => t.id === activeTenant.id));

