/**
 * Validates that soft-deleted configurations are excluded from the configurations list.
 *
 * This test ensures that when a configuration in ai_mall_backend_configurations is soft-deleted (by setting deleted_at),
 * it is excluded from future results when listing configurations. This enforces the intended behavior of soft-deletion,
 * ensuring admins and business logic do not see archived configuration items when searching or paginating through configs.
 *
 * Step-by-step process:
 * 1. Create a new configuration using the POST /aiMallBackend/configurations endpoint.
 * 2. Soft-delete the created configuration using DELETE /aiMallBackend/configurations/{id} (which sets deleted_at on the record).
 * 3. Run the configuration list (PATCH /aiMallBackend/configurations) to retrieve the current configs.
 * 4. Assert that the previously soft-deleted configuration is absent from the returned list (its id must not appear).
 * 5. If present, fail the test; if absent, confirm expected filtering of soft-deleted rows.
 */
export async function test_api_aiMallBackend_configurations_test_list_configurations_excludes_soft_deleted(connection: api.IConnection) {
  // 1. Create a new configuration
  const createInput: IAiMallBackendConfigurations.ICreate = {
    name: RandomGenerator.alphabets(8),
    value: RandomGenerator.alphabets(8),
    channel_id: null, // Global config
    section_id: null, // Not section-bound
    description: RandomGenerator.paragraph()(1),
  };
  const config = await api.functional.aiMallBackend.configurations.create(connection, {
    body: createInput,
  });
  typia.assert(config);

  // 2. Soft-delete the configuration by id
  const erased = await api.functional.aiMallBackend.configurations.erase(connection, {
    id: config.id,
  });
  typia.assert(erased);
  TestValidator.predicate("soft deletion updated deleted_at")(!!erased.deleted_at);

  // 3. Run configuration list (should NOT include soft-deleted configs)
  const page = await api.functional.aiMallBackend.configurations.index(connection, {
    body: {}, // Empty filter = all configs
  });
  typia.assert(page);

  // 4. Assert soft-deleted config is absent
  const exists = page.data.some((row) => row.id === config.id);
  TestValidator.equals("soft-deleted config should be excluded")(exists)(false);
}

