/**
 * Test paginated listing and advanced filtering of API gateways.
 *
 * This end-to-end test verifies that the API Gateway listing endpoint supports
 * multi-channel filtering, enabled/disabled state, and pagination, and that
 * records soft-deleted (i.e., with deleted_at set) do not appear in the list.
 *
 * Workflow:
 * 1. Create two distinct channels (A, B)
 * 2. Register several API gateways:
 *    - For Channel A: at least two gateways (different route_base/domain/enabled combinations)
 *    - For Channel B: at least one gateway (distinct values)
 *    - Use different combinations of enabled (true/false)
 * 3. Soft-delete one API gateway from channel A
 * 4. Request a list of API gateways with no filter:
 *    - Assert all non-deleted gateways for both channels are present
 *    - Assert soft-deleted gateway does NOT appear
 * 5. Filter: channel_id=Channel A
 *    - Assert only gateways belonging to Channel A (except deleted) appear
 * 6. Filter: enabled=false
 *    - Assert all disabled (but not deleted) gateways for all channels appear
 * 7. Pagination: Pick a limit less than total records, confirm pagination metadata and that correct subset of gateways are returned
 * 8. Spot-search: filter by route_base or domain (pick a single value), confirm only matching gateway(s) are returned
 */
export async function test_api_aiMallBackend_test_list_api_gateways_with_pagination_and_filters(
  connection: api.IConnection,
) {
  // 1. Create channels A and B
  const tenant_id_1 = typia.random<string & tags.Format<"uuid">>();
  const tenant_id_2 = typia.random<string & tags.Format<"uuid">>();

  const channelA = await api.functional.aiMallBackend.channels.create(connection, {
    body: { tenant_id: tenant_id_1, code: "channelA", name: "Channel A", enabled: true } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channelA);

  const channelB = await api.functional.aiMallBackend.channels.create(connection, {
    body: { tenant_id: tenant_id_2, code: "channelB", name: "Channel B", enabled: true } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channelB);

  // 2. Create gateway records
  const gwA1 = await api.functional.aiMallBackend.apiGateways.create(connection, {
    body: { channel_id: channelA.id, route_base: "/api/gwa1", domain: "a1.example.com", enabled: true } satisfies IAiMallBackendApiGateways.ICreate,
  });
  typia.assert(gwA1);

  const gwA2 = await api.functional.aiMallBackend.apiGateways.create(connection, {
    body: { channel_id: channelA.id, route_base: "/api/gwa2", domain: "a2.example.com", enabled: false } satisfies IAiMallBackendApiGateways.ICreate,
  });
  typia.assert(gwA2);

  const gwB1 = await api.functional.aiMallBackend.apiGateways.create(connection, {
    body: { channel_id: channelB.id, route_base: "/api/gwb1", domain: "b1.example.com", enabled: true } satisfies IAiMallBackendApiGateways.ICreate,
  });
  typia.assert(gwB1);

  // 3. Soft-delete one gateway (gwA1)
  const erasedGwA1 = await api.functional.aiMallBackend.apiGateways.erase(connection, { id: gwA1.id });
  typia.assert(erasedGwA1);
  TestValidator.predicate("soft-deleted gwA1 deleted_at non-null")(!!erasedGwA1.deleted_at);

  // 4. List all gateways (no filters)
  const allResult = await api.functional.aiMallBackend.apiGateways.index(connection, { body: {} });
  typia.assert(allResult);
  const allIds = allResult.data.map(x => x.id);
  TestValidator.notEquals("gwA1 is not listed")(true)(allIds.includes(gwA1.id));
  TestValidator.equals("gwA2 present")(true)(allIds.includes(gwA2.id));
  TestValidator.equals("gwB1 present")(true)(allIds.includes(gwB1.id));

  // 5. Filter: channel_id=channelA.id
  const chAResult = await api.functional.aiMallBackend.apiGateways.index(connection, { body: { channel_id: channelA.id } });
  typia.assert(chAResult);
  TestValidator.equals("only gwA2 for channel A")(
    [gwA2.id]
  )(chAResult.data.map(x => x.id).sort());

  // 6. Filter: enabled=false
  const disabledResult = await api.functional.aiMallBackend.apiGateways.index(connection, { body: { enabled: false } });
  typia.assert(disabledResult);
  TestValidator.equals("only gwA2 is disabled")([gwA2.id])(disabledResult.data.map(x => x.id));

  // 7. Pagination: limit=1, first page
  const pagedResult1 = await api.functional.aiMallBackend.apiGateways.index(connection, { body: {} });
  typia.assert(pagedResult1);
  if (pagedResult1.data.length >= 2) {
    const pagedManual = await api.functional.aiMallBackend.apiGateways.index(connection, { body: {} });
    TestValidator.predicate("pagination pages >= 1")(
      pagedManual.pagination.pages >= 1,
    );
    TestValidator.predicate("total record count matches sum of page sizes")(
      pagedManual.data.length <= pagedManual.pagination.limit,
    );
  }

  // 8. Spot-search: by domain
  const spotResult = await api.functional.aiMallBackend.apiGateways.index(connection, { body: { domain: "a2.example.com" } });
  typia.assert(spotResult);
  TestValidator.equals("search by domain returns gwA2 only")(
    [gwA2.id]
  )(spotResult.data.map(x => x.id));
}

