import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";
import type { IPageIAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendSections";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test searching for sections with multiple filter and pagination criteria.
 *
 * This test validates the AI Mall Backend's section search endpoint under 
 * realistic scenarios, including use of channel_id, code, name, enabled 
 * filters, and pagination options.
 *
 * Steps:
 * 1. Create two distinct channel records (unique tenant_ids for isolation).
 * 2. For each channel, create at least three section records with varying codes, names, and enabled/disabled status.
 * 3. Perform search with channel_id filter to confirm only that channel's sections are returned.
 * 4. Perform search with combined code, name, and enabled filters for fine-grained search.
 * 5. Check paginated response, verifying limit/page and correct total count/records returned.
 * 6. Ensure all pagination metadata is accurate and all filter criteria are observed in results.
 * 7. Verify no extraneous results (from other channels, codes, status) appear in filtered responses.
 */
export async function test_api_aiMallBackend_test_search_sections_with_multiple_filters_and_pagination(
  connection: api.IConnection,
) {
  // Step 1: Create two unique channels
  const tenantId1: string = typia.random<string & tags.Format<"uuid">>();
  const tenantId2: string = typia.random<string & tags.Format<"uuid">>();
  const channel1 = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenantId1,
      code: "web" + RandomGenerator.alphaNumeric(3),
      name: "Web Channel " + RandomGenerator.alphaNumeric(4),
      enabled: true
    } satisfies IAiMallBackendChannels.ICreate
  });
  typia.assert(channel1);
  const channel2 = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenantId2,
      code: "mobile" + RandomGenerator.alphaNumeric(3),
      name: "Mobile Channel " + RandomGenerator.alphaNumeric(4),
      enabled: false
    } satisfies IAiMallBackendChannels.ICreate
  });
  typia.assert(channel2);

  // Step 2: For each channel, create 3 sections with various filters
  const sections: IAiMallBackendSections[] = [];
  for (const [channel, enabledOptions] of [
    [channel1, [true, false, true]],
    [channel2, [false, false, true]]
  ] as const) {
    for (let i = 0; i < 3; ++i) {
      const section = await api.functional.aiMallBackend.sections.create(connection, {
        body: {
          channel_id: channel.id,
          code: `${channel.code}_section_${i}`,
          name: `${channel.name} Section ${i}`,
          enabled: enabledOptions[i]
        } satisfies IAiMallBackendSections.ICreate
      });
      typia.assert(section);
      sections.push(section);
    }
  }

  // Step 3: Perform search with channel_id for channel1
  const page1 = await api.functional.aiMallBackend.sections.index(connection, {
    body: { channel_id: channel1.id } satisfies IAiMallBackendSections.IRequest
  });
  typia.assert(page1);
  TestValidator.predicate("all results have channel_id")(page1.data.every(s => s.channel_id === channel1.id));
  TestValidator.predicate("only channel1 sections")(page1.pagination.records === 3);

  // Step 4: Search with code, name, enabled filter (on second section of channel2)
  const section2OfChannel2 = sections.find(s => s.channel_id === channel2.id && s.code.endsWith("_section_1"));
  TestValidator.predicate("section2OfChannel2 found")(!!section2OfChannel2);
  const page2 = await api.functional.aiMallBackend.sections.index(connection, {
    body: {
      channel_id: channel2.id,
      code: section2OfChannel2!.code,
      name: section2OfChannel2!.name,
      enabled: section2OfChannel2!.enabled
    } satisfies IAiMallBackendSections.IRequest
  });
  typia.assert(page2);
  TestValidator.equals("returned correct one")(page2.data.length)(1);
  TestValidator.equals("code")(page2.data[0].code)(section2OfChannel2!.code);
  TestValidator.equals("name")(page2.data[0].name)(section2OfChannel2!.name);
  TestValidator.equals("enabled")(page2.data[0].enabled)(section2OfChannel2!.enabled);

  // Step 5: Pagination - fetch first page with limit 2
  const paged = await api.functional.aiMallBackend.sections.index(connection, {
    body: { channel_id: channel1.id, page: 1, limit: 2 } satisfies IAiMallBackendSections.IRequest
  });
  typia.assert(paged);
  TestValidator.equals("current page")(paged.pagination.current)(1);
  TestValidator.equals("limit")(paged.pagination.limit)(2);
  TestValidator.equals("records")(paged.pagination.records)(3);
  TestValidator.equals("pages")(paged.pagination.pages)(2);
  TestValidator.equals("number of results")(paged.data.length)(2);
  TestValidator.predicate("all on correct channel")(paged.data.every(s => s.channel_id === channel1.id));

  // Step 6: Pagination, fetch second page and verify rest returned
  const paged2 = await api.functional.aiMallBackend.sections.index(connection, {
    body: { channel_id: channel1.id, page: 2, limit: 2 } satisfies IAiMallBackendSections.IRequest
  });
  typia.assert(paged2);
  TestValidator.equals("current page")(paged2.pagination.current)(2);
  TestValidator.equals("limit")(paged2.pagination.limit)(2);
  TestValidator.equals("number of results")(paged2.data.length)(1);
  TestValidator.predicate("all on correct channel")(paged2.data.every(s => s.channel_id === channel1.id));
  TestValidator.equals("records")(paged2.pagination.records)(3);

  // Step 7: Negative filter - enabled=false, channel2
  const onlyDisabled = await api.functional.aiMallBackend.sections.index(connection, {
    body: { channel_id: channel2.id, enabled: false } satisfies IAiMallBackendSections.IRequest
  });
  typia.assert(onlyDisabled);
  TestValidator.predicate("all disabled")(onlyDisabled.data.every(s => s.enabled === false));
  TestValidator.predicate("all channel2")(onlyDisabled.data.every(s => s.channel_id === channel2.id));
}

