import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IPageIAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendChannels";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Validate paginated and filtered channel listing for multi-tenant ai_mall_backend_channels.
 *
 * This test ensures correct operation of paginated search and filtering for channels under different tenants. It focuses on covering search by tenant, code, name, enabled status, and creation date/time ranges, as well as verifying pagination metadata and the exclusion of soft-deleted records.
 *
 * Workflow:
 * 1. Create two distinct tenants.
 * 2. Under tenant1, create at least two channels (one enabled, one disabled).
 * 3. Under tenant2, create at least one channel.
 * 4. Soft-delete one channel under tenant1 (for deleted filter testing).
 * 5. List all channels (default) - verify all active (non-deleted) channels for both tenants are returned.
 * 6. Filter by tenant_id - verify only that tenant's channels are returned.
 * 7. Filter by code and name - verify results are accurate and case-sensitive.
 * 8. Filter by enabled status (true/false) - verify only appropriately-flagged channels are returned.
 * 9. Filter by created_at_from/created_at_to - verify only correct date range is returned.
 * 10. Paginate results (limit/offset) - verify pagination metadata and partial result delivery.
 * 11. Verify soft-deleted channel is never included in results unless include_deleted=true (which is not covered by main path).
 */
export async function test_api_aiMallBackend_test_list_channels_with_search_and_pagination(
  connection: api.IConnection,
) {
  // Step 1: Create Tenant 1
  const tenant1 = await api.functional.aiMallBackend.tenants.create(connection, {
    body: {
      code: RandomGenerator.alphabets(8),
      name: RandomGenerator.name(),
      archived: false,
    } satisfies IAiMallBackendTenants.ICreate,
  });
  typia.assert(tenant1);
  // Step 2: Create Tenant 2
  const tenant2 = await api.functional.aiMallBackend.tenants.create(connection, {
    body: {
      code: RandomGenerator.alphabets(8),
      name: RandomGenerator.name(),
      archived: false,
    } satisfies IAiMallBackendTenants.ICreate,
  });
  typia.assert(tenant2);

  // Step 3: Create Channels for Tenant 1
  const channel1 = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenant1.id,
      code: "web_channel1", // static for deterministic search
      name: "Web Channel Alpha",
      enabled: true,
    } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channel1);
  const channel2 = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenant1.id,
      code: "mobile_channel1",
      name: "Mobile Channel Alpha",
      enabled: false,
    } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channel2);
  // Step 4: Create Channel for Tenant 2
  const channel3 = await api.functional.aiMallBackend.channels.create(connection, {
    body: {
      tenant_id: tenant2.id,
      code: "web_channel2",
      name: "Web Channel Beta",
      enabled: true,
    } satisfies IAiMallBackendChannels.ICreate,
  });
  typia.assert(channel3);

  // Step 5: Soft-delete channel2
  await api.functional.aiMallBackend.channels.erase(connection, { id: channel2.id });
  // Confirm erased (deleted_at set), but do NOT expect to include in searches below

  // 6: List all channels (should return active, non-deleted)
  let resultAll = await api.functional.aiMallBackend.channels.index(connection, { body: {} });
  typia.assert(resultAll);
  // Should include channel1 and channel3, exclude channel2
  const activeIds = resultAll.data.map((c) => c.id);
  TestValidator.predicate("all results active, deleted_at is null")(resultAll.data.every((c) => !c.deleted_at));
  TestValidator.predicate("channel1 returned")(activeIds.includes(channel1.id));
  TestValidator.predicate("channel3 returned")(activeIds.includes(channel3.id));
  TestValidator.predicate("channel2 not returned")(activeIds.includes(channel2.id) === false);

  // 7: Filter by tenant_id (tenant1)
  let filterTenant1 = await api.functional.aiMallBackend.channels.index(connection, {
    body: { tenant_id: tenant1.id },
  });
  typia.assert(filterTenant1);
  TestValidator.predicate("only tenant1 active channels")(filterTenant1.data.every((c) => c.tenant_id === tenant1.id && !c.deleted_at));
  TestValidator.predicate("channel1 present")(filterTenant1.data.map((c) => c.id).includes(channel1.id));
  TestValidator.predicate("channel2 not present")(filterTenant1.data.map((c) => c.id).includes(channel2.id) === false);

  // 8: Filter by code (web_channel1)
  let filterCode = await api.functional.aiMallBackend.channels.index(connection, {
    body: { code: "web_channel1" },
  });
  typia.assert(filterCode);
  TestValidator.predicate("all results match code")(filterCode.data.every((c) => c.code === "web_channel1" && !c.deleted_at));
  TestValidator.predicate("channel1 present")(filterCode.data.map((c) => c.id).includes(channel1.id));

  // 9: Filter by name (Web Channel Alpha)
  let filterName = await api.functional.aiMallBackend.channels.index(connection, {
    body: { name: "Web Channel Alpha" },
  });
  typia.assert(filterName);
  TestValidator.predicate("all results match name")(filterName.data.every((c) => c.name === "Web Channel Alpha" && !c.deleted_at));
  TestValidator.predicate("channel1 present")(filterName.data.map((c) => c.id).includes(channel1.id));

  // 10: Filter by enabled (true)
  let filterEnabled = await api.functional.aiMallBackend.channels.index(connection, {
    body: { enabled: true },
  });
  typia.assert(filterEnabled);
  TestValidator.predicate("all enabled")(filterEnabled.data.every((c) => c.enabled === true && !c.deleted_at));
  TestValidator.predicate("channel1 present")(filterEnabled.data.map((c) => c.id).includes(channel1.id));
  TestValidator.predicate("channel3 present")(filterEnabled.data.map((c) => c.id).includes(channel3.id));
  TestValidator.predicate("channel2 not present")(filterEnabled.data.map((c) => c.id).includes(channel2.id) === false);

  // 10b: Filter by enabled (false)
  let filterDisabled = await api.functional.aiMallBackend.channels.index(connection, {
    body: { enabled: false },
  });
  typia.assert(filterDisabled);
  TestValidator.predicate("all disabled")(filterDisabled.data.every((c) => c.enabled === false && !c.deleted_at));

  // 11: Filter by created_at_from/created_at_to
  // Use channel1's created_at as reference
  let filterCreatedFrom = await api.functional.aiMallBackend.channels.index(connection, {
    body: { created_at_from: channel1.created_at },
  });
  typia.assert(filterCreatedFrom);
  TestValidator.predicate("all results created at >= reference")(filterCreatedFrom.data.every((c) => c.created_at >= channel1.created_at && !c.deleted_at));

  let filterCreatedTo = await api.functional.aiMallBackend.channels.index(connection, {
    body: { created_at_to: channel1.created_at },
  });
  typia.assert(filterCreatedTo);
  TestValidator.predicate("all results created at <= reference")(filterCreatedTo.data.every((c) => c.created_at <= channel1.created_at && !c.deleted_at));

  // 12: Pagination: limit 1, page 1
  let paged1 = await api.functional.aiMallBackend.channels.index(connection, {
    body: { limit: 1, page: 1 },
  });
  typia.assert(paged1);
  TestValidator.equals("pagination.limit")(paged1.pagination.limit)(1);
  TestValidator.equals("pagination.current")(paged1.pagination.current)(1);
  // At least 1 item (since channels exist)
  TestValidator.predicate("at least 1 result")(paged1.data.length === 1);
  // 13: Pagination: page 2 (should return next result)
  let paged2 = await api.functional.aiMallBackend.channels.index(connection, {
    body: { limit: 1, page: 2 },
  });
  typia.assert(paged2);
  TestValidator.equals("pagination.limit")(paged2.pagination.limit)(1);
  TestValidator.equals("pagination.current")(paged2.pagination.current)(2);
  // 0 or 1 result (depending on # of active channels)
  TestValidator.predicate("0 or 1 result")(paged2.data.length >= 0 && paged2.data.length <= 1);
}

