import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendConfigurations } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendConfigurations";
import type { IPageIAiMallBackendConfigurations } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendConfigurations";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validate pagination of ai_mall_backend_configurations records.
 *
 * This test checks that the paginated configuration listing endpoint correctly
 * slices a dataset that exceeds a single page, validating both record counts
 * and navigation logic.
 *
 * Process:
 *
 * 1. Create N = (default page size + extra) configuration rows using unique
 *    deterministic names.
 * 2. Retrieve first result page (expecting full page size) and assert correct
 *    number and order.
 * 3. Retrieve second result page (expecting remainder records) and assert count
 *    and non-overlap.
 * 4. Assert pagination metadata: current, limit, records, and pages fields behave
 *    as expected.
 *
 * This ensures that no records are skipped or duplicated, and navigation fields
 * allow clients to fully traverse the dataset.
 */
export async function test_api_aiMallBackend_configurations_test_pagination_of_configuration_results(
  connection: api.IConnection,
) {
  // Default page size to test (common pattern, typically 20; adjust as real default known)
  const PAGE_SIZE = 20;
  const TOTAL = PAGE_SIZE + 5;

  // 1. Create TOTAL deterministic unique configuration records
  const configs: IAiMallBackendConfigurations[] = [];
  for (let i = 0; i < TOTAL; ++i) {
    const created = await api.functional.aiMallBackend.configurations.create(
      connection,
      {
        body: {
          name: `test-pagination-config-${i}`,
          value: `v${i}`,
          description: `pagination test record #${i}`,
        } satisfies IAiMallBackendConfigurations.ICreate,
      },
    );
    typia.assert(created);
    configs.push(created);
  }

  // Sort for expected order (descending by created_at, to match typical backend default)
  const expectedSorted = [...configs].sort((a, b) =>
    a.created_at > b.created_at ? -1 : 1,
  );

  // 2. Retrieve first page (assume empty filter; default page/limit applies)
  const page1 = await api.functional.aiMallBackend.configurations.index(
    connection,
    {
      body: {} satisfies IAiMallBackendConfigurations.IRequest,
    },
  );
  typia.assert(page1);

  // Validate record count and expected presence (default: PAGE_SIZE)
  TestValidator.equals("first page count")(page1.data.length)(PAGE_SIZE);

  const page1Ids = new Set(page1.data.map((x) => x.id));
  for (const id of expectedSorted.slice(0, PAGE_SIZE).map((c) => c.id)) {
    TestValidator.predicate("first page contains expected config")(
      page1Ids.has(id),
    );
  }

  // 3. Retrieve second page (attempt to pass page/limit fields if IRequest allows; otherwise adapt as API supports)
  //    API interface as given in materials does not show explicit page/limit, so may not support this directly.
  //    If SDK/type is updated, adjust accordingly. For now, this is illustrative; adjust for real param names.
  //    This template assumes an extension of IRequest with 'page' and 'limit'.
  const page2 = await api.functional.aiMallBackend.configurations.index(
    connection,
    {
      body: {
        // If these fields are not in schema, the call should be adapted/removed.
        ...(typeof ({} as IAiMallBackendConfigurations.IRequest).page !==
        "undefined"
          ? { page: 2, limit: PAGE_SIZE }
          : {}),
      } as IAiMallBackendConfigurations.IRequest,
    },
  );
  typia.assert(page2);

  // Validate page2 record count (should be 5 from N = PAGE_SIZE + 5)
  TestValidator.equals("second page count")(page2.data.length)(5);

  // Ensure no overlap between page1 and page2 configs
  const page2Ids = new Set(page2.data.map((x) => x.id));
  for (const id of page2Ids) {
    TestValidator.predicate("no overlap between page 1 and 2")(
      !page1Ids.has(id),
    );
  }

  // Validate ordering in page2 (descending created_at)
  for (let i = 0; i < page2.data.length - 1; ++i) {
    const left = page2.data[i].created_at;
    const right = page2.data[i + 1].created_at;
    TestValidator.predicate("descending created_at order")(left >= right);
  }

  // Check pagination metadata matches expectations for page 2
  TestValidator.equals("metadata: page 2")(page2.pagination.current)(2);
  TestValidator.equals("metadata: page size")(page2.pagination.limit)(
    PAGE_SIZE,
  );
  TestValidator.equals("metadata: total records")(page2.pagination.records)(
    TOTAL,
  );
  TestValidator.equals("metadata: number of pages")(page2.pagination.pages)(2);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendApiGateways } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendApiGateways";

/**
 * Validate that the API gateway creation endpoint correctly enforces required
 * field validation.
 *
 * This test ensures that missing or null required fields (channel_id,
 * route_base, domain, enabled) in the API gateway creation payload are
 * appropriately rejected by the backend with clear validation errors. It uses a
 * real, created channel for reference (to ensure the only error is the missing
 * field, not an invalid channel).
 *
 * Steps:
 *
 * 1. Create a valid channel record - needed to test valid channel_id scenarios.
 * 2. For each required field in IAiMallBackendApiGateways.ICreate:
 *
 *    - Omit the field and attempt API gateway creation; expect validation failure.
 *    - Set the field to null; expect validation failure.
 *    - Always validate that errors are thrown, but not their content per guidelines.
 */
export async function test_api_aiMallBackend_test_create_api_gateway_with_missing_required_fields(
  connection: api.IConnection,
) {
  // 1. Create a valid channel to reference (so channel_id test is meaningful)
  const validChannel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphabets(6),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(validChannel);

  // Construct a valid gateway creation payload
  const validGatewayBody = {
    channel_id: validChannel.id,
    route_base: "/api/v1",
    domain: `gateway${RandomGenerator.alphaNumeric(4)}.testdomain.com`,
    enabled: true,
  } satisfies IAiMallBackendApiGateways.ICreate;

  const requiredFields = [
    "channel_id",
    "route_base",
    "domain",
    "enabled",
  ] as const;

  // Attempt creation with each required field omitted
  for (const field of requiredFields) {
    const payload = { ...validGatewayBody };
    // @ts-expect-error: deliberate omission for validation test
    delete payload[field];
    await TestValidator.error(`missing required field: ${field}`)(async () => {
      await api.functional.aiMallBackend.apiGateways.create(connection, {
        body: payload as any, // Blocked by runtime validation
      });
    });
  }

  // Attempt creation with each required field set to null
  for (const field of requiredFields) {
    const payload = { ...validGatewayBody, [field]: null };
    await TestValidator.error(`null required field: ${field}`)(async () => {
      await api.functional.aiMallBackend.apiGateways.create(connection, {
        body: payload as any, // Blocked by runtime validation
      });
    });
  }
}

