import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";
import type { IPageIAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendTenants";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validate tenant filtering logic by code and name search.
 *
 * This test ensures that the backend tenant listing API correctly supports
 * filtering tenants by their code and name fields. It covers platform admin
 * requirements for finding tenants in multi-tenant SaaS environments.
 *
 * Steps:
 *
 * 1. Create at least 3 tenants, with intentionally overlapping fragments in codes
 *    and names (e.g., code: "tenantAlpha", name: "Alpha Holdings").
 * 2. Issue filtered tenant-list requests using 'code' and/or 'name' properties,
 *    both with exact and partial values, to verify proper substring/text
 *    matching.
 * 3. Assert that the filter only returns tenants matching the filter parameters,
 *    and does not include non-matching tenants.
 * 4. Confirm that case-insensitive and partial matches work as expected, according
 *    to the endpoint/documentation.
 *
 * This is crucial to assure admins can search effectively, maintain operational
 * efficiency, and tenant onboarding is auditable. It also prevents unwanted
 * tenant leakage or search confusion.
 */
export async function test_api_aiMallBackend_test_list_tenants_with_code_and_name_filter(
  connection: api.IConnection,
) {
  // 1. Create 3 tenants with similar fragments but different codes/names
  const tenants: IAiMallBackendTenants[] = [];

  const baseTenants = [
    { code: "tenantAlpha", name: "Alpha Holdings" },
    { code: "tenantBeta", name: "Beta Technologies" },
    { code: "tenantGamma", name: "Gamma Group" },
  ];

  for (const base of baseTenants) {
    const tenant = await api.functional.aiMallBackend.tenants.create(
      connection,
      {
        body: {
          code: base.code,
          name: base.name,
          archived: false,
        } satisfies IAiMallBackendTenants.ICreate,
      },
    );
    typia.assert(tenant);
    tenants.push(tenant);
  }

  // 2. Filter by code substring (e.g., "Alpha")
  const codeFilter = "Alpha";
  const byCode = await api.functional.aiMallBackend.tenants.index(connection, {
    body: {
      code: codeFilter,
      limit: 30,
    },
  });
  typia.assert(byCode);
  TestValidator.predicate("all results must include code filter")(
    byCode.data.every(
      (t) => t.code.includes(codeFilter) || t.name.includes(codeFilter),
    ),
  );
  TestValidator.equals("should match tenantAlpha")(
    byCode.data.some(
      (t) => t.code === "tenantAlpha" && t.name === "Alpha Holdings",
    ),
  )(true);
  TestValidator.equals("should not match tenantBeta or tenantGamma")(
    byCode.data.some(
      (t) => t.code === "tenantBeta" || t.code === "tenantGamma",
    ),
  )(false);

  // 3. Filter by name substring (e.g., "Tech")
  const nameFilter = "Tech";
  const byName = await api.functional.aiMallBackend.tenants.index(connection, {
    body: {
      name: nameFilter,
      limit: 30,
    },
