import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";
import type { IPageIAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendTenants";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test that tenant listing/pagination with the archived status filter behaves
 * correctly.
 *
 * This test covers the following sequence:
 *
 * 1. Create two tenants with unique codes and names: one active (archived=false)
 *    and one archived (archived=true).
 * 2. Execute the tenant listing API with the 'archived' filter set to true and
 *    confirm only the archived tenant is returned.
 * 3. Execute the tenant listing API with the 'archived' filter set to false and
 *    confirm only the active tenant is returned.
 * 4. Execute the tenant listing API without the 'archived' filter and confirm both
 *    tenants are included in the response data.
 *
 * This ensures that the archiving workflow is properly honored for list
 * filtering, and that archived tenants are neither lost nor shown incorrectly
 * in the active-only view.
 */
export async function test_api_aiMallBackend_tenants_test_list_tenants_with_archived_status_filter(
  connection: api.IConnection,
) {
  // 1. Create an active tenant
  const activeTenant = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: `active-${RandomGenerator.alphabets(8)}`,
        name: `Active Tenant ${RandomGenerator.alphabets(8)}`,
        archived: false,
      },
    },
  );
  typia.assert(activeTenant);

  // 2. Create an archived tenant
  const archivedTenant = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: `archived-${RandomGenerator.alphabets(8)}`,
        name: `Archived Tenant ${RandomGenerator.alphabets(8)}`,
        archived: true,
      },
    },
  );
  typia.assert(archivedTenant);

  // 3. List tenants with archived=true
  const archivedList = await api.functional.aiMallBackend.tenants.index(
    connection,
    {
      body: { archived: true, limit: 10, page: 1 },
    },
  );
  typia.assert(archivedList);
  TestValidator.predicate("archived list includes only archived tenants")(
    archivedList.data.every((t) => t.archived === true),
  );
  TestValidator.predicate("archived tenant is found")(
    archivedList.data.some((t) => t.id === archivedTenant.id),
  );

  // 4. List tenants with archived=false
  const activeList = await api.functional.aiMallBackend.tenants.index(
    connection,
    {
      body: { archived: false, limit: 10, page: 1 },
    },
  );
  typia.assert(activeList);
  TestValidator.predicate("active list includes only active tenants")(
    activeList.data.every((t) => t.archived === false),
  );
  TestValidator.predicate("active tenant is found")(
    activeList.data.some((t) => t.id === activeTenant.id),
  );

  // 5. List tenants WITHOUT archived flag (should include both tenants)
  const allList = await api.functional.aiMallBackend.tenants.index(connection, {
    body: { limit: 100, page: 1 },
  });
  typia.assert(allList);
  TestValidator.predicate("response includes active tenant")(
    allList.data.some((t) => t.id === activeTenant.id),
  );
  TestValidator.predicate("response includes archived tenant")(
    allList.data.some((t) => t.id === archivedTenant.id),
  );
}

