/**
 * Validate retrieving detailed information for an existing tenant by id.
 *
 * This test confirms that a tenant, once created, can be looked up precisely by
 * its unique id. It verifies the returned details match the data originally
 * inserted, plus system-generated fields.
 *
 * Steps:
 *
 * 1. Create a tenant with specific code, name, and archived values
 * 2. Retrieve the tenant by its returned id
 * 3. Assert that code, name, archived, and id are identical to those given during
 *    creation
 * 4. Assert that created_at and updated_at are valid ISO8601 date-time strings and
 *    not empty
 * 5. Optionally, ensure updated_at is equal to or after created_at
 */
export async function test_api_aiMallBackend_tenants_test_get_tenant_by_valid_id(
  connection: api.IConnection,
) {
  // 1. Create a tenant with unique test inputs
  const input: IAiMallBackendTenants.ICreate = {
    code: `tenant_${RandomGenerator.alphaNumeric(6)}`,
    name: `Tenant Test ${RandomGenerator.alphabets(4)}`,
    archived: false,
  };
  const created = await api.functional.aiMallBackend.tenants.create(
    connection,
    { body: input },
  );
  typia.assert(created);

  // 2. Retrieve the tenant by id
  const detail = await api.functional.aiMallBackend.tenants.at(connection, {
    id: created.id,
  });
  typia.assert(detail);

  // 3. Assert equality of all fields
  TestValidator.equals("id")(detail.id)(created.id);
  TestValidator.equals("code")(detail.code)(input.code);
  TestValidator.equals("name")(detail.name)(input.name);
  TestValidator.equals("archived")(detail.archived)(input.archived);

  // 4. Assert created_at/updated_at are valid non-empty ISO8601 strings
  TestValidator.predicate("created_at is valid ISO8601")(
    !!detail.created_at && !Number.isNaN(Date.parse(detail.created_at)),
  );
  TestValidator.predicate("updated_at is valid ISO8601")(
    !!detail.updated_at && !Number.isNaN(Date.parse(detail.updated_at)),
  );

  // 5. updated_at should be >= created_at
  TestValidator.predicate("updated_at >= created_at")(
    Date.parse(detail.updated_at) >= Date.parse(detail.created_at),
  );
}

