  // 3. Update the tenant by id with the new data
  const updated = await api.functional.aiMallBackend.tenants.update(
    connection,
    {
      id: original.id,
      body: updateInput,
    },
  );
  typia.assert(updated);

  // 4. Validate changed/unchanged fields as per business rules
  TestValidator.equals("id unchanged")(updated.id)(original.id);
  TestValidator.equals("code updated")(updated.code)(updateInput.code);
  TestValidator.equals("name updated")(updated.name)(updateInput.name);
  TestValidator.equals("archived updated")(updated.archived)(
    updateInput.archived,
  );
  TestValidator.equals("created_at unchanged")(updated.created_at)(
    original.created_at,
  );
  TestValidator.predicate("updated_at is refreshed")(
    updated.updated_at !== original.updated_at,
  );
}

