import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Test archiving an AI Mall Backend tenant (soft delete: set archived=true)
 *
 * This test function verifies that the tenant archiving endpoint correctly
 * updates the tenant's `archived` flag and does not alter any other details. It
 * covers the following workflow:
 *
 * 1. Create a tenant entity (archived=false) to ensure we have an active target
 *    for archiving.
 * 2. Call the archive endpoint (DELETE /aiMallBackend/tenants/{id}).
 * 3. Validate that the response entity has `archived=true`, and all other fields
 *    (id, code, name, created_at, updated_at) remain unchanged except
 *    `archived` and possible `updated_at`.
 * 4. Ensures business policy of auditability, correct archiving, and no field
 *    corruption.
 */
export async function test_api_aiMallBackend_tenants_test_archive_tenant_successfully(
  connection: api.IConnection,
) {
  // 1. Create a new tenant (archived=false)
  const tenantInput: IAiMallBackendTenants.ICreate = {
    code: RandomGenerator.alphabets(10),
    name: `TestTenant-${RandomGenerator.alphabets(5)}`,
    archived: false,
  };
  const createdTenant: IAiMallBackendTenants =
    await api.functional.aiMallBackend.tenants.create(connection, {
      body: tenantInput,
    });
  typia.assert(createdTenant);
  TestValidator.equals("archived is false on creation")(createdTenant.archived)(
    false,
  );

  // 2. Archive the tenant (DELETE = set archived=true)
  const archivedTenant: IAiMallBackendTenants =
    await api.functional.aiMallBackend.tenants.erase(connection, {
      id: createdTenant.id,
    });
  typia.assert(archivedTenant);

  // 3. Validate all fields remain, except archived/updated_at business logic
  TestValidator.equals("archived must be true")(archivedTenant.archived)(true);
  TestValidator.equals("id must remain the same")(archivedTenant.id)(
    createdTenant.id,
  );
  TestValidator.equals("code must remain the same")(archivedTenant.code)(
    createdTenant.code,
  );
  TestValidator.equals("name must remain the same")(archivedTenant.name)(
    createdTenant.name,
  );
  TestValidator.equals("created_at must remain the same")(
    archivedTenant.created_at,
  )(createdTenant.created_at);
  // updated_at is allowed to change (should be >= original)
  TestValidator.predicate("updated_at should be updated or equal")(
    new Date(archivedTenant.updated_at).getTime() >=
      new Date(createdTenant.updated_at).getTime(),
  );
}

