import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IPageIAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendChannels";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Validate paginated and filtered channel listing for multi-tenant
 * ai_mall_backend_channels.
 *
 * This test ensures correct operation of paginated search and filtering for
 * channels under different tenants. It covers search by tenant, code, name,
 * enabled status, creation date/time ranges, as well as verifies pagination
 * metadata and the exclusion of soft-deleted records.
 *
 * Workflow:
 *
 * 1. Create two distinct tenants.
 * 2. Under tenant1, create at least two channels (one enabled, one disabled).
 * 3. Under tenant2, create at least one channel.
 * 4. Soft-delete one channel under tenant1 (for deleted filter testing).
 * 5. List all channels (default) - verify all active (non-deleted) channels for
 *    both tenants are returned.
 * 6. Filter by tenant_id - verify only that tenant's channels are returned.
 * 7. Filter by code and name - verify results are accurate and case-sensitive.
 * 8. Filter by enabled status (true/false) - verify only appropriately-flagged
 *    channels are returned.
 * 9. Filter by created_at_from/created_at_to - verify only correct date range is
 *    returned.
 * 10. Paginate results (limit/page) - verify pagination metadata and partial result
 *     delivery.
 * 11. Verify soft-deleted channel is never included in results.
 */
export async function test_api_aiMallBackend_test_list_channels_with_search_and_pagination(
  connection: api.IConnection,
) {
  // Step 1: Create Tenant 1
  const tenant1 = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: RandomGenerator.alphabets(8),
        name: RandomGenerator.name(),
        archived: false,
      } satisfies IAiMallBackendTenants.ICreate,
    },
  );
  typia.assert(tenant1);

  // Step 2: Create Tenant 2
  const tenant2 = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: RandomGenerator.alphabets(8),
        name: RandomGenerator.name(),
        archived: false,
      } satisfies IAiMallBackendTenants.ICreate,
    },
  );
  typia.assert(tenant2);

  // Step 3: Create Channels for Tenant 1
  const channel1 = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenant1.id,
        code: "web_channel1", // static for deterministic search
        name: "Web Channel Alpha",
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel1);

  const channel2 = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenant1.id,
        code: "mobile_channel1",
        name: "Mobile Channel Alpha",
        enabled: false,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel2);

  // Step 4: Create Channel for Tenant 2
  const channel3 = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenant2.id,
        code: "web_channel2",
        name: "Web Channel Beta",
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel3);

  // Step 5: Soft-delete channel2 (for filtering test)
  await api.functional.aiMallBackend.channels.erase(connection, {
    id: channel2.id,
  });

  // Step 6: List all channels (should return active, non-deleted only)
  const resultAll = await api.functional.aiMallBackend.channels.index(
    connection,
    { body: {} },
  );
  typia.assert(resultAll);
  const activeIds = resultAll.data.map((c) => c.id);
  TestValidator.predicate("all channels returned are non-deleted")(
    resultAll.data.every((c) => !c.deleted_at),
  );
  TestValidator.predicate("channel1 present")(activeIds.includes(channel1.id));
  TestValidator.predicate("channel3 present")(activeIds.includes(channel3.id));
  TestValidator.predicate("channel2 absent")(
    activeIds.includes(channel2.id) === false,
  );

  // Step 7: Filter by tenant_id (tenant1)
  const filterTenant1 = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { tenant_id: tenant1.id },
    },
  );
  typia.assert(filterTenant1);
  TestValidator.predicate("all returned are tenant1, non-deleted")(
    filterTenant1.data.every(
      (c) => c.tenant_id === tenant1.id && !c.deleted_at,
    ),
  );
  TestValidator.predicate("channel1 present")(
    filterTenant1.data.map((c) => c.id).includes(channel1.id),
  );
  TestValidator.predicate("channel2 absent")(
    filterTenant1.data.map((c) => c.id).includes(channel2.id) === false,
  );

  // Step 8: Filter by code (web_channel1)
  const filterCode = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { code: "web_channel1" },
    },
  );
  typia.assert(filterCode);
  TestValidator.predicate("all results have code web_channel1")(
    filterCode.data.every((c) => c.code === "web_channel1" && !c.deleted_at),
  );
  TestValidator.predicate("channel1 present")(
    filterCode.data.map((c) => c.id).includes(channel1.id),
  );

  // Step 9: Filter by name (Web Channel Alpha)
  const filterName = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { name: "Web Channel Alpha" },
    },
  );
  typia.assert(filterName);
  TestValidator.predicate("all results have name Web Channel Alpha")(
    filterName.data.every(
      (c) => c.name === "Web Channel Alpha" && !c.deleted_at,
    ),
  );
  TestValidator.predicate("channel1 present")(
    filterName.data.map((c) => c.id).includes(channel1.id),
  );

  // Step 10: Filter by enabled=true
  const filterEnabled = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { enabled: true },
    },
  );
  typia.assert(filterEnabled);
  TestValidator.predicate("all enabled are true")(
    filterEnabled.data.every((c) => c.enabled === true && !c.deleted_at),
  );
  TestValidator.predicate("channel1 present")(
    filterEnabled.data.map((c) => c.id).includes(channel1.id),
  );
  TestValidator.predicate("channel3 present")(
    filterEnabled.data.map((c) => c.id).includes(channel3.id),
  );
  TestValidator.predicate("channel2 absent")(
    filterEnabled.data.map((c) => c.id).includes(channel2.id) === false,
  );

  // Step 10b: Filter by enabled=false (should not return deleted)
  const filterDisabled = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { enabled: false },
    },
  );
  typia.assert(filterDisabled);
  TestValidator.predicate("all enabled are false, not deleted")(
    filterDisabled.data.every((c) => c.enabled === false && !c.deleted_at),
  );

  // Step 11: Filter by created_at_from (should only return newer records)
  const filterCreatedFrom = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { created_at_from: channel1.created_at },
    },
  );
  typia.assert(filterCreatedFrom);
  TestValidator.predicate("all result created_at >= channel1")(
    filterCreatedFrom.data.every(
      (c) => c.created_at >= channel1.created_at && !c.deleted_at,
    ),
  );

  // Step 11b: Filter by created_at_to (should only return older records)
  const filterCreatedTo = await api.functional.aiMallBackend.channels.index(
    connection,
    {
      body: { created_at_to: channel1.created_at },
    },
  );
  typia.assert(filterCreatedTo);
  TestValidator.predicate("all result created_at <= channel1")(
    filterCreatedTo.data.every(
      (c) => c.created_at <= channel1.created_at && !c.deleted_at,
    ),
  );

  // Step 12: Pagination - limit 1, page 1
  const paged1 = await api.functional.aiMallBackend.channels.index(connection, {
    body: { limit: 1, page: 1 },
  });
  typia.assert(paged1);
  TestValidator.equals("pagination.limit")(paged1.pagination.limit)(1);
  TestValidator.equals("pagination.current")(paged1.pagination.current)(1);
  TestValidator.predicate("at least 1 result")(paged1.data.length === 1);

  // Step 13: Pagination - page 2
  const paged2 = await api.functional.aiMallBackend.channels.index(connection, {
    body: { limit: 1, page: 2 },
  });
  typia.assert(paged2);
  TestValidator.equals("pagination.limit")(paged2.pagination.limit)(1);
  TestValidator.equals("pagination.current")(paged2.pagination.current)(2);
  TestValidator.predicate("0 or 1 result")(
    paged2.data.length >= 0 && paged2.data.length <= 1,
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IPageIAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendChannels";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validate that searching ai_mall_backend_channels with impossible/mismatching
 * criteria returns an empty result set.
 *
 * This test ensures that the paginated channel search returns the correct
 * pagination metadata and an empty data array when no records match the filter
 * conditions. Various guaranteed-miss filters are used: a random UUID for
 * 'tenant_id', a code that cannot exist, a future-dated created_at window, and
 * an unlikely enabled state. The test confirms that the returned data array is
 * empty and that pagination metadata still returns valid (zero-record)
 * information.
 *
 * 1. Build a filter request with unique/nonexistent values (random tenant_id,
 *    unmatchable code, created_at_from set well beyond the present).
 * 2. Call api.functional.aiMallBackend.channels.index with the filter in the
 *    request body.
 * 3. Assert that output.data is an empty array ([]).
 * 4. Assert that output.pagination.records is 0 and output.pagination.pages is 0
 *    or 1 per API logic (if limit permits zero-page, otherwise, should be 1
 *    page with zero records).
 */
export async function test_api_aiMallBackend_test_list_channels_with_no_results_found(
  connection: api.IConnection,
) {
  // 1. Prepare guaranteed-no-match filter input
  const impossibleFilter = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(), // unlikely to match
    code: "__no_such_code__", // code that cannot exist
    enabled: false, // if all channels are enabled, this misses
    created_at_from: "2099-12-31T23:59:59.000Z", // future date, no records can match
    name: null,
    created_at_to: null,
    updated_at_from: null,
    updated_at_to: null,
    include_deleted: null,
    page: 1,
    limit: 10,
  } satisfies IAiMallBackendChannels.IRequest;

  // 2. Call API with filter
  const output = await api.functional.aiMallBackend.channels.index(connection, {
    body: impossibleFilter,
  });
  typia.assert(output);

  // 3. Validate result set is empty
  TestValidator.equals("empty data")(output.data)([]);
  // 4. Validate pagination metadata reflects zero records
  TestValidator.equals("zero records")(output.pagination.records)(0);
  // Acceptable for pages to be 0 or 1 depending on limit logic
  TestValidator.predicate("pages are 0 or 1")(
    output.pagination.pages === 0 || output.pagination.pages === 1,
  );
}

