/**
 * Validate successful detail retrieval of an existing channel record.
 *
 * This test ensures that the detail GET endpoint for an
 * ai_mall_backend_channels entity returns correct, complete information for a
 * valid ID, after performing realistic prerequisite steps:
 *
 * 1. Create the necessary tenant (multi-tenancy enforced)  ensures channel can be
 *    associated with a valid tenant.
 * 2. Create a channel for that tenant via the admin onboarding process.
 * 3. Retrieve the channel using its unique ID via the detail endpoint.
 * 4. Verify all fields in the GET response match what was recorded at creation,
 *    including: id, tenant_id, code, name, enabled, created_at, updated_at,
 *    deleted_at.
 */
export async function test_api_aiMallBackend_channels_test_get_channel_details_successful(
  connection: api.IConnection,
) {
  // 1. Create a tenant for channel association
  const tenant = await api.functional.aiMallBackend.tenants.create(connection, {
    body: {
      code: RandomGenerator.alphaNumeric(8),
      name: RandomGenerator.alphabets(10),
      archived: false,
    } satisfies IAiMallBackendTenants.ICreate,
  });
  typia.assert(tenant);

  // 2. Create a channel for the above tenant
  const channelCreateInput: IAiMallBackendChannels.ICreate = {
    tenant_id: tenant.id,
    code: RandomGenerator.alphaNumeric(6),
    name: RandomGenerator.alphabets(12),
    enabled: true,
  };

  const createdChannel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: channelCreateInput,
    },
  );
  typia.assert(createdChannel);

  // 3. Retrieve the channel details using the ID
  const fetchedChannel = await api.functional.aiMallBackend.channels.at(
    connection,
