  // 4. Check the response fields for completeness and correctness
  TestValidator.equals("id matches")(fetchedChannel.id)(createdChannel.id);
  TestValidator.equals("tenant_id matches")(fetchedChannel.tenant_id)(
    tenant.id,
  );
  TestValidator.equals("code matches")(fetchedChannel.code)(
    createdChannel.code,
  );
  TestValidator.equals("name matches")(fetchedChannel.name)(
    createdChannel.name,
  );
  TestValidator.equals("enabled matches")(fetchedChannel.enabled)(
    createdChannel.enabled,
  );
  TestValidator.equals("deleted_at is null (not deleted)")(
    fetchedChannel.deleted_at,
  )(null);
  TestValidator.predicate("created_at populated")(
    typeof fetchedChannel.created_at === "string" &&
      fetchedChannel.created_at.length > 0,
  );
  TestValidator.predicate("updated_at populated")(
    typeof fetchedChannel.updated_at === "string" &&
      fetchedChannel.updated_at.length > 0,
  );
}

