import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendTenants } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendTenants";

/**
 * Test updating an existing channel's key properties and validating
 * persistence.
 *
 * This test verifies that you can successfully update a channel's code, name,
 * enabled status, and tenant_idincluding cross-tenant transferusing the AI
 * Mall Backend's channel update endpoint. It also checks that the updated_at
 * timestamp properly reflects the update operation.
 *
 * Steps:
 *
 * 1. Create two unique tenants (A and B) to simulate cross-tenant channel
 *    movement.
 * 2. Create a channel under tenant A with initial values (code, name, enabled).
 * 3. Record the original updated_at timestamp of the created channel.
 * 4. Update the channel:
 *
 * - Change code, name, enabled (flip value), and tenant_id to tenant B's id.
 *
 * 5. Retrieve the updated channel entity (output of update) and validate:
 *
 * - All fields were updated as requested
 * - The tenant_id is now B's id
 * - The updated_at timestamp has changed from its original value
 * - All other non-updated fields are unchanged
 */
export async function test_api_aiMallBackend_channels_test_update_channel_with_valid_data(
  connection: api.IConnection,
) {
  // 1. Create two tenants (A, B)
  const tenantA = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: RandomGenerator.alphabets(8),
        name: `Tenant A ${RandomGenerator.alphabets(6)}`,
        archived: false,
      } satisfies IAiMallBackendTenants.ICreate,
    },
  );
  typia.assert(tenantA);
  const tenantB = await api.functional.aiMallBackend.tenants.create(
    connection,
    {
      body: {
        code: RandomGenerator.alphabets(8),
        name: `Tenant B ${RandomGenerator.alphabets(6)}`,
        archived: false,
      } satisfies IAiMallBackendTenants.ICreate,
    },
  );
  typia.assert(tenantB);

  // 2. Create a channel for tenantA
  const originalChannel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenantA.id,
        code: RandomGenerator.alphabets(8),
        name: `ChannelX ${RandomGenerator.alphabets(6)}`,
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(originalChannel);

  // 3. Record original updated_at
  const originalUpdatedAt = originalChannel.updated_at;

  // 4. Prepare new update values
  const updatedCode = RandomGenerator.alphabets(10);
  const updatedName = `ChannelY ${RandomGenerator.alphabets(7)}`;
  const updatedEnabled = !originalChannel.enabled;
  const updatedTenantId = tenantB.id;

  // 5. Update the channel
  const updatedChannel = await api.functional.aiMallBackend.channels.update(
    connection,
    {
      id: originalChannel.id,
      body: {
        code: updatedCode,
        name: updatedName,
        enabled: updatedEnabled,
        tenant_id: updatedTenantId,
      } satisfies IAiMallBackendChannels.IUpdate,
    },
  );
  typia.assert(updatedChannel);

  // 6. Assert all changes were persisted correctly
  TestValidator.equals("code updated")(updatedChannel.code)(updatedCode);
  TestValidator.equals("name updated")(updatedChannel.name)(updatedName);
  TestValidator.equals("enabled updated")(updatedChannel.enabled)(
    updatedEnabled,
  );
  TestValidator.equals("tenant id updated")(updatedChannel.tenant_id)(
    updatedTenantId,
  );
  TestValidator.notEquals("updated_at changed")(updatedChannel.updated_at)(
    originalUpdatedAt,
  );

  // 7. Validate non-updated fields are unchanged
  TestValidator.equals("id remains")(updatedChannel.id)(originalChannel.id);
  TestValidator.equals("created_at remains")(updatedChannel.created_at)(
    originalChannel.created_at,
  );
}

