import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";
import type { IAiMallBackendConfigurations } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendConfigurations";

/**
 * Test the creation of a new configuration scoped to a specific channel and
 * section.
 *
 * This test validates the creation workflow for establishing an atomic
 * configuration record that is associated with both a valid channel and a
 * section under that channel. The business context ensures that channel and
 * section entities are set up before configuration creation, which enforces
 * proper foreign key relationships and business scoping as required by the
 * multi-tenant system.
 *
 * Step-by-step process:
 *
 * 1. Create a channel (using /aiMallBackend/channels) and capture its id
 *    (channel_id).
 * 2. Create a section (using /aiMallBackend/sections) with the channel_id from
 *    above and capture its id (section_id).
 * 3. Call the configurations creation endpoint (/aiMallBackend/configurations)
 *    with a unique name and value, referencing the collected channel_id and
 *    section_id.
 * 4. Assert the response object:
 *
 *    - All provided fields (name, value, channel_id, section_id, description if
 *         supplied) match what was submitted
 *    - Deleted_at is null
 *    - Created_at and updated_at are valid date-time strings (not null or empty)
 *
 * Business rules:
 *
 * - The configuration must be properly scoped: both channel_id and section_id
 *   must reference real entities.
 * - There must be no soft-deletion; on creation, deleted_at is null.
 *
 * This function ensures data integrity and proper scoping for configurations in
 * the atomic configuration system.
 */
export async function test_api_aiMallBackend_configurations_test_create_configuration_with_valid_channel_and_section_scope(
  connection: api.IConnection,
) {
  // 1. Create a valid channel
  const channelInput: IAiMallBackendChannels.ICreate = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(),
    code: `test_channel_${RandomGenerator.alphaNumeric(8)}`,
    name: `Test Channel ${RandomGenerator.alphabets(6)}`,
    enabled: true,
  };
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    { body: channelInput },
  );
  typia.assert(channel);
  TestValidator.equals("channel: code match")(channel.code)(channelInput.code);
  TestValidator.equals("channel: tenant_id match")(channel.tenant_id)(
    channelInput.tenant_id,
  );
  TestValidator.equals("channel: enabled match")(channel.enabled)(
    channelInput.enabled,
  );
  TestValidator.equals("channel: deleted_at is null")(channel.deleted_at)(null);

  // 2. Create a section under the channel
  const sectionInput: IAiMallBackendSections.ICreate = {
    channel_id: channel.id,
    code: `section_${RandomGenerator.alphaNumeric(8)}`,
    name: `Section ${RandomGenerator.alphabets(6)}`,
    enabled: true,
  };
  const section = await api.functional.aiMallBackend.sections.create(
    connection,
    { body: sectionInput },
  );
  typia.assert(section);
  TestValidator.equals("section: code match")(section.code)(sectionInput.code);
  TestValidator.equals("section: channel_id match")(section.channel_id)(
    sectionInput.channel_id,
  );
  TestValidator.equals("section: enabled match")(section.enabled)(
    sectionInput.enabled,
  );
  TestValidator.equals("section: deleted_at is null")(section.deleted_at)(null);

  // 3. Create a configuration using channel_id and section_id
  const configInput: IAiMallBackendConfigurations.ICreate = {
    channel_id: channel.id,
    section_id: section.id,
    name: `config_${RandomGenerator.alphaNumeric(10)}`,
    value: RandomGenerator.alphaNumeric(16),
    description: `E2E Test config for channel/section scope: ${RandomGenerator.paragraph()(1)}`,
  };
  const config = await api.functional.aiMallBackend.configurations.create(
    connection,
    { body: configInput },
  );
  typia.assert(config);
  TestValidator.equals("config: name match")(config.name)(configInput.name);
  TestValidator.equals("config: value match")(config.value)(configInput.value);
  TestValidator.equals("config: channel_id match")(config.channel_id)(
    configInput.channel_id,
  );
  TestValidator.equals("config: section_id match")(config.section_id)(
    configInput.section_id,
  );
  TestValidator.equals("config: description match")(config.description)(
    configInput.description,
  );
  TestValidator.equals("config: deleted_at is null")(config.deleted_at)(null);
  TestValidator.predicate("config: created_at is valid")(
    !!config.created_at && typeof config.created_at === "string",
  );
  TestValidator.predicate("config: updated_at is valid")(
    !!config.updated_at && typeof config.updated_at === "string",
  );
}

