  const channelId = typia.random<string & tags.Format<"uuid">>();
  const sectionId = typia.random<string & tags.Format<"uuid">>();
  const duplicateName = RandomGenerator.alphaNumeric(12);
  const value1 = RandomGenerator.alphaNumeric(16);
  const value2 = RandomGenerator.alphaNumeric(16);

  // 2. Create the original configuration (should succeed)
  const original = await api.functional.aiMallBackend.configurations.create(
    connection,
    {
      body: {
        channel_id: channelId,
        section_id: sectionId,
        name: duplicateName,
        value: value1,
        description: "Original config for uniqueness test.",
      } satisfies IAiMallBackendConfigurations.ICreate,
    },
  );
  typia.assert(original);

