    async () => {
      await api.functional.aiMallBackend.configurations.create(connection, {
        body: {
          channel_id: channelId,
          section_id: sectionId,
          name: duplicateName, // Same name and scope as before
          value: value2, // Value can differ
          description: "Intentional duplicate for negative test case.",
        } satisfies IAiMallBackendConfigurations.ICreate,
      });
    },
  );
}

