import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendConfigurations } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendConfigurations";

/**
 * Validates updating an AI Mall backend configuration's value and description.
 *
 * This test ensures that configuration update flows perform as designed:
 *
 * 1. First, a configuration row is created with an initial value and description.
 * 2. The configuration is then updated, changing the value and adding/updating the
 *    description.
 * 3. The test verifies that updated fields reflect in the API response and
 *    'updated_at' timestamp is newer.
 * 4. Edge cases: Update with only 'value', and with both 'value' and
 *    'description'; also validate 'updated_at' returns a later timestamp, and
 *    all field values match after update.
 */
export async function test_api_aiMallBackend_configurations_test_update_configuration_value_and_description(
  connection: api.IConnection,
) {
  // 1. Create initial configuration entity
  const createInput: IAiMallBackendConfigurations.ICreate = {
    name: `e2e_config_${RandomGenerator.alphaNumeric(8)}`,
    value: "original-value",
    description: "original config description",
    // channel_id/section_id left null for global config
  };
  const created = await api.functional.aiMallBackend.configurations.create(
    connection,
    { body: createInput },
  );
  typia.assert(created);

  // 2. Update the configuration's value and description
  const updateInput: IAiMallBackendConfigurations.IUpdate = {
    value: "updated-value",
    description: "updated config description",
  };
  await new Promise((res) => setTimeout(res, 10)); // ensure updated_at is later than created_at
  const updated = await api.functional.aiMallBackend.configurations.update(
    connection,
    {
      id: created.id,
      body: updateInput,
    },
  );
  typia.assert(updated);

  // 3. Validate that fields have changed and updated_at is later
  TestValidator.notEquals("updated_at changed")(created.updated_at)(
    updated.updated_at,
  );
  TestValidator.equals("value updated")(updated.value)(updateInput.value);
  TestValidator.equals("description updated")(updated.description)(
    updateInput.description,
  );

  // 4. Also test updating just the value (not description)
  const updateValueOnly: IAiMallBackendConfigurations.IUpdate = {
    value: "value-only-change",
  };
  await new Promise((res) => setTimeout(res, 10));
  const updated2 = await api.functional.aiMallBackend.configurations.update(
    connection,
    {
      id: created.id,
      body: updateValueOnly,
    },
  );
  typia.assert(updated2);
  TestValidator.notEquals("updated_at incremented")(updated.updated_at)(
    updated2.updated_at,
  );
  TestValidator.equals("description retained")(updated2.description)(
    updateInput.description,
  );
  TestValidator.equals("value changed again")(updated2.value)(
    updateValueOnly.value,
  );
}

