/**
 * Validate that updating a soft-deleted configuration record is forbidden.
 *
 * This test ensures that after a configuration is created and then soft-deleted
 * (by setting its deleted_at timestamp via the soft delete API), any attempt to
 * update that record fails with an error. This behavior preserves auditability
 * and protects the logical integrity of configurations, as soft-deleted rows
 * are intended to be immutable and hidden from ordinary modification.
 *
 * Step-by-step process:
 *
 * 1. Create a new configuration record with sample values
 * 2. Soft-delete the configuration record just created
 * 3. Attempt to update the soft-deleted configuration record
 * 4. Assert that the update operation fails (throws an error as expected)
 */
export async function test_api_aiMallBackend_configurations_test_update_configuration_on_soft_deleted_record_fails(
  connection: api.IConnection,
) {
  // 1. Create a configuration
  const config = await api.functional.aiMallBackend.configurations.create(
    connection,
    {
      body: {
        name: `e2e_test_softdelete_${RandomGenerator.alphaNumeric(8)}`,
        value: RandomGenerator.alphabets(8),
        channel_id: null,
        section_id: null,
        description: "Test configuration for soft-delete update constraint.",
      } satisfies IAiMallBackendConfigurations.ICreate,
    },
  );
  typia.assert(config);

  // 2. Soft-delete the configuration
  const erased = await api.functional.aiMallBackend.configurations.erase(
    connection,
    {
      id: config.id,
    },
  );
  typia.assert(erased);
  TestValidator.predicate("deleted_at is set after erase")(
    typeof erased.deleted_at === "string" && erased.deleted_at.length > 0,
  );

  // 3. Attempt to update the soft-deleted configuration
  //    This should fail due to business logic (not type error)
  await TestValidator.error("fail to update soft-deleted record")(() =>
    api.functional.aiMallBackend.configurations.update(connection, {
      id: config.id,
      body: {
        value: RandomGenerator.alphabets(12),
      } satisfies IAiMallBackendConfigurations.IUpdate,
    }),
  );
}

