  const configCreateInput: IAiMallBackendConfigurations.ICreate = {
    name: `config_delete_test_${RandomGenerator.alphaNumeric(8)}`,
    value: "test-value",
    // Optionals: leave null to verify default handling
    channel_id: null,
    section_id: null,
    description: null,
  };
