  TestValidator.equals("id unchanged after delete")(deletedConfig.id)(
    configuration.id,
  );
  TestValidator.equals("name unchanged after delete")(deletedConfig.name)(
    configuration.name,
  );
  TestValidator.equals("value unchanged after delete")(deletedConfig.value)(
    configuration.value,
  );
