/**
 * Validate creation of a new section tied to a valid business channel, with all
 * mandatory fields supplied.
 *
 * This test is required to confirm that the platform supports modular content
 * management (section composition) in a multi-tenant, multi-channel context. A
 * section cannot exist without an associated channel, enforcing strong foreign
 * key constraints and modular design. Timestamp attributes must be set for
 * audit trail.
 *
 * Test Procedure:
 *
 * 1. Create a business channel with all required properties.
 * 2. Use its id as channel_id to create a new section, providing code, name, and
 *    enabled=true/false.
 * 3. Validate the returned section record matches input and business rules, and
 *    audit columns (created_at, updated_at) are set to valid date-time
 *    strings.
 */
export async function test_api_aiMallBackend_test_create_section_with_valid_channel_and_full_data(
  connection: api.IConnection,
) {
  // 1. Create a channel as a required dependency for the section
  const tenantId = typia.random<string & tags.Format<"uuid">>();
  const channelCode = RandomGenerator.alphaNumeric(8);
  const channelName = RandomGenerator.alphabets(10);
  const channelEnabled = true;

  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenantId,
        code: channelCode,
        name: channelName,
        enabled: channelEnabled,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);
  TestValidator.equals("channel code")(channel.code)(channelCode);
  TestValidator.equals("channel tenant_id")(channel.tenant_id)(tenantId);
  TestValidator.equals("channel enabled")(channel.enabled)(channelEnabled);
  TestValidator.predicate("valid created_at timestamp")(
    !!Date.parse(channel.created_at),
  );
  TestValidator.predicate("valid updated_at timestamp")(
    !!Date.parse(channel.updated_at),
  );
  TestValidator.equals("channel deleted_at is null or undefined")(
    channel.deleted_at ?? null,
  )(null);

  // 2. Create a new section under this channel
  const sectionCode = RandomGenerator.alphaNumeric(6);
  const sectionName = RandomGenerator.alphabets(15);
  const sectionEnabled = true;
  const section = await api.functional.aiMallBackend.sections.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        code: sectionCode,
        name: sectionName,
        enabled: sectionEnabled,
      } satisfies IAiMallBackendSections.ICreate,
    },
  );
  typia.assert(section);
  TestValidator.equals("section channel_id")(section.channel_id)(channel.id);
  TestValidator.equals("section code")(section.code)(sectionCode);
  TestValidator.equals("section name")(section.name)(sectionName);
  TestValidator.equals("section enabled")(section.enabled)(sectionEnabled);
  TestValidator.predicate("section created_at timestamp valid")(
    !!Date.parse(section.created_at),
  );
  TestValidator.predicate("section updated_at timestamp valid")(
    !!Date.parse(section.updated_at),
  );
  TestValidator.equals("section deleted_at is null or undefined")(
    section.deleted_at ?? null,
  )(null);
}

