import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";

/**
 * Validate input validation logic for section creation by omitting required
 * fields.
 *
 * This test checks the API's contract enforcement for required fields of the
 * section creation endpoint. It iteratively omits each required field
 * ('channel_id', 'code', 'name', or 'enabled') in
 * IAiMallBackendSections.ICreate and attempts to create a section. For each
 * missing field case, this should result in a validation error response
 * specifying the missing property, confirming robust input validation.
 *
 * Steps:
 *
 * 1. Create a valid channel (channel must exist to test everything except missing
 *    'channel_id')
 * 2. For each required field in section creation: a. Attempt to create a section
 *    with that field explicitly set to undefined b. Verify that the API
 *    responds with a validation error mentioning the missing property
 * 3. (Sanity) Create a section with all required fields to demonstrate happy path
 */
export async function test_api_aiMallBackend_test_create_section_missing_required_fields(
  connection: api.IConnection,
) {
  // Step 1: Create a valid channel
  const tenantId = typia.random<string & tags.Format<"uuid">>();
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenantId,
        code: `web_${RandomGenerator.alphaNumeric(4)}`,
        name: `Web Channel ${RandomGenerator.alphabets(5)}`,
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // Step 2: Test missing required fields
  const validSection: IAiMallBackendSections.ICreate = {
    channel_id: channel.id,
    code: `main_${RandomGenerator.alphaNumeric(3)}`,
    name: `Main Section ${RandomGenerator.alphabets(4)}`,
    enabled: true,
  };

  const fields: (keyof IAiMallBackendSections.ICreate)[] = [
    "channel_id",
    "code",
    "name",
    "enabled",
  ];

  for (const field of fields) {
    // Create a shallow copy and set one required field to undefined (removes it from JSON in serialization)
    const sectionInput: any = { ...validSection };
    delete sectionInput[field];
    await TestValidator.error(`Missing required field: ${field}`)(async () => {
      await api.functional.aiMallBackend.sections.create(connection, {
        body: sectionInput,
      });
    });
  }

  // Step 3: Happy path - all fields present
  const section = await api.functional.aiMallBackend.sections.create(
    connection,
    {
      body: validSection,
    },
  );
  typia.assert(section);
}

