import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";
import type { IPageIAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendSections";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Test search and pagination of modular backend sections with combined filters.
 *
 * This E2E test verifies the aiMallBackend.sections.index endpoint can:
 *
 * - Correctly filter sections by channel, code, name, and enabled/disabled status
 * - Support paginated results with accurate navigation metadata
 *
 * Business context:
 *
 * - Multi-tenant channels are created, each with multiple modular sections for
 *   realistic coverage.
 * - Filtering covers channel, code, name, enabled status, and combined scenarios,
 *   ensuring precise match.
 * - Pagination accuracy is validated including meta: total count, page, limit,
 *   data quantity.
 *
 * Workflow:
 *
 * 1. Create two channels using unique tenants for isolation.
 * 2. For each channel, create three sections with distinct code, name, and enabled
 *    status.
 * 3. Search by channel_id to confirm channel isolation.
 * 4. Filter by channel_id + code + name + enabled for granular test.
 * 5. Check pagination with limit/page and verify correctness of navigation meta &
 *    result set.
 * 6. Fetch subsequent page and confirm expected number of results.
 * 7. Test enabled=false filter for one channel to verify negative filter and data
 *    separation.
 */
export async function test_api_aiMallBackend_test_search_sections_with_multiple_filters_and_pagination(
  connection: api.IConnection,
) {
  // 1. Create two unique channels (channel1 enabled, channel2 disabled)
  const tenantId1 = typia.random<string & tags.Format<"uuid">>();
  const tenantId2 = typia.random<string & tags.Format<"uuid">>();
  const channel1 = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenantId1,
        code: "web" + RandomGenerator.alphaNumeric(3),
        name: "Web Channel " + RandomGenerator.alphaNumeric(4),
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel1);
  const channel2 = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: tenantId2,
        code: "mobile" + RandomGenerator.alphaNumeric(3),
        name: "Mobile Channel " + RandomGenerator.alphaNumeric(4),
        enabled: false,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel2);

  // 2. For each channel, create three sections with different config
  const sections: IAiMallBackendSections[] = [];
  for (const [channel, enabledList] of [
    [channel1, [true, false, true]],
    [channel2, [false, false, true]],
  ] as const) {
    for (let idx = 0; idx < 3; ++idx) {
      const section = await api.functional.aiMallBackend.sections.create(
        connection,
        {
          body: {
            channel_id: channel.id,
            code: `${channel.code}_section_${idx}`,
            name: `${channel.name} Section ${idx}`,
            enabled: enabledList[idx],
          } satisfies IAiMallBackendSections.ICreate,
        },
      );
      typia.assert(section);
      sections.push(section);
    }
  }

  // 3. Search by channel_id to confirm all and only that channel's sections
  const pagePerChannel = await api.functional.aiMallBackend.sections.index(
    connection,
    {
      body: {
        channel_id: channel1.id,
      } satisfies IAiMallBackendSections.IRequest,
    },
  );
  typia.assert(pagePerChannel);
  TestValidator.predicate("All results belong to channel1")(
    pagePerChannel.data.every((r) => r.channel_id === channel1.id),
  );
  TestValidator.equals("Correct result count for channel1")(
    pagePerChannel.data.length,
  )(3);
  TestValidator.equals("Pagination records")(pagePerChannel.pagination.records)(
    3,
  );

  // 4. Filter by channel_id, code, name, enabled: expect single match
  const section1of2 = sections.find(
    (s) => s.channel_id === channel2.id && s.code.endsWith("_section_1"),
  );
  TestValidator.predicate("section1of2 exists")(!!section1of2);
  const filtered = await api.functional.aiMallBackend.sections.index(
    connection,
    {
      body: {
        channel_id: channel2.id,
        code: section1of2!.code,
        name: section1of2!.name,
        enabled: section1of2!.enabled,
      } satisfies IAiMallBackendSections.IRequest,
    },
  );
  typia.assert(filtered);
  TestValidator.equals("Only one match for fully filtered search")(
    filtered.data.length,
  )(1);
  TestValidator.equals("Filtered code matches")(filtered.data[0].code)(
    section1of2!.code,
  );
  TestValidator.equals("Filtered name matches")(filtered.data[0].name)(
    section1of2!.name,
  );
  TestValidator.equals("Filtered enabled state matches")(
    filtered.data[0].enabled,
