    channel2.id,
  );

  // 5. Search channel1 with pagination limit=2, page=1
  const pagedFirst = await api.functional.aiMallBackend.sections.index(
    connection,
    {
      body: {
        channel_id: channel1.id,
        page: 1,
        limit: 2,
      } satisfies IAiMallBackendSections.IRequest,
    },
  );
  typia.assert(pagedFirst);
  TestValidator.equals("Page 1")(pagedFirst.pagination.current)(1);
  TestValidator.equals("Page size")(pagedFirst.pagination.limit)(2);
  TestValidator.equals("Pagination total")(pagedFirst.pagination.records)(3);
  TestValidator.equals("Pagination pages")(pagedFirst.pagination.pages)(2);
  TestValidator.equals("Data count on page 1")(pagedFirst.data.length)(2);
  TestValidator.predicate("All on channel1")(
    pagedFirst.data.every((r) => r.channel_id === channel1.id),
  );

  // 6. Fetch page 2 for channel1, should be 1 left
  const pagedSecond = await api.functional.aiMallBackend.sections.index(
    connection,
    {
      body: {
        channel_id: channel1.id,
        page: 2,
        limit: 2,
      } satisfies IAiMallBackendSections.IRequest,
    },
  );
  typia.assert(pagedSecond);
  TestValidator.equals("Page 2")(pagedSecond.pagination.current)(2);
  TestValidator.equals("Page size")(pagedSecond.pagination.limit)(2);
  TestValidator.equals("Data count on page 2")(pagedSecond.data.length)(1);
  TestValidator.equals("Pagination total")(pagedSecond.pagination.records)(3);
  TestValidator.predicate("All on channel1")(
    pagedSecond.data.every((r) => r.channel_id === channel1.id),
  );

  // 7. Enabled=false sections for channel2 only
  const channel2disabled = await api.functional.aiMallBackend.sections.index(
    connection,
    {
      body: {
        channel_id: channel2.id,
        enabled: false,
      } satisfies IAiMallBackendSections.IRequest,
    },
  );
  typia.assert(channel2disabled);
  TestValidator.equals("All channel2")(
    channel2disabled.data.every((r) => r.channel_id === channel2.id),
  )(true);
  TestValidator.equals("All disabled")(
    channel2disabled.data.every((r) => r.enabled === false),
  )(true);
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";
import type { IPageIAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIAiMallBackendSections";
import type { IPage } from "@ORGANIZATION/PROJECT-api/lib/structures/IPage";

/**
 * Validates that soft-deleted sections are excluded from section search by
 * default.
 *
 * Ensures that when searching for sections, those with a non-null `deleted_at`
 * are NOT returned unless explicitly included with `include_deleted`. This test
 * covers the full workflow of creating, deleting (soft), and searching sections
 * under a channel.
 *
 * Steps:
 *
 * 1. Create a new channel as prerequisite for section creation.
 * 2. Create multiple sections under the new channel.
 * 3. Soft-delete (set deleted_at) for some sections.
 * 4. Search sections for that channel without `include_deleted` (default
 *    behavior).
 * 5. Assert soft-deleted sections are omitted. Assert live sections are returned.
 */
export async function test_api_aiMallBackend_test_search_sections_excludes_soft_deleted_by_default(
  connection: api.IConnection,
) {
  // 1. Create a new backend channel
  const channelBody = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(),
    code: RandomGenerator.alphaNumeric(10),
    name: RandomGenerator.paragraph()(2),
    enabled: true,
  } satisfies IAiMallBackendChannels.ICreate;
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    { body: channelBody },
  );
  typia.assert(channel);

  // 2. Create multiple (5) sections in the channel
  const sections = await ArrayUtil.asyncRepeat(5)(async (i) => {
    const body = {
      channel_id: channel.id,
      code: RandomGenerator.alphaNumeric(8) + i,
      name: RandomGenerator.paragraph()(2) + i,
      enabled: true,
    } satisfies IAiMallBackendSections.ICreate;
    const section = await api.functional.aiMallBackend.sections.create(
      connection,
      { body },
    );
    typia.assert(section);
    return section;
  });

  // 3. Soft-delete the first two sections
  const deletedSections = await Promise.all([
    api.functional.aiMallBackend.sections.erase(connection, {
      id: sections[0].id,
    }),
    api.functional.aiMallBackend.sections.erase(connection, {
      id: sections[1].id,
    }),
  ]);
  deletedSections.forEach((d) => typia.assert(d));

  // 4. Search sections for this channel. DO NOT set include_deleted, expect only live shown.
  const searchBody = {
    channel_id: channel.id,
    include_deleted: false,
  } satisfies IAiMallBackendSections.IRequest;
  const searchResult = await api.functional.aiMallBackend.sections.index(
    connection,
    { body: searchBody },
  );
  typia.assert(searchResult);

  // 5A. Assert NO soft-deleted section is present
  const ids = searchResult.data.map((x) => x.id);
  for (const deleted of deletedSections) {
    TestValidator.predicate("deleted section not returned")(
      !ids.includes(deleted.id),
    );
  }

  // 5B. Assert all live sections ARE present
  for (const liveSection of sections.slice(2)) {
    TestValidator.predicate("live section is present")(
      ids.includes(liveSection.id),
    );
  }
}

