 *
 * Steps:
 *
 * 1. Create a business channel (ai_mall_backend_channels)
 * 2. Create a section under that channel
 * 3. PUT (update) the section with new code/name/enabled values
 * 4. Verify returned section reflects changes
 * 5. Confirm updated_at differs from original
 * 6. Assert types and data integrity
 */
export async function test_api_aiMallBackend_sections_test_update_section_with_valid_data(
  connection: api.IConnection,
) {
  // 1. Create a business channel for the section
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(7),
        name: "Test Channel " + RandomGenerator.alphaNumeric(4),
        enabled: true,
