  // 3. Update section fields (code, name, enabled)
  const newCode = RandomGenerator.alphaNumeric(10);
  const newName = "Updated Section " + RandomGenerator.alphaNumeric(5);
  const newEnabled = false;

  const updated = await api.functional.aiMallBackend.sections.update(
