import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";

/**
 * Validate input schema enforcement when updating an ai_mall_backend_sections
 * record with invalid data.
 *
 * This test ensures the API endpoint for updating a section rejects invalid
 * data and reports validation errors whenever input fails the schema rules (eg.
 * setting an empty string to required textual fields, or assigning an invalid
 * type to a boolean, etc). This enforces strong model correctness and prevents
 * schema drift or configuration corruption via the admin interface.
 *
 * Test steps:
 *
 * 1. Create a business channel (to meet foreign key/channel section relationship
 *    requirements)
 * 2. Create a valid section under the channel to serve as the update target
 * 3. Attempt to update the section with one or more invalid payloads: a. code: ""
 *    (empty string) - should be rejected b. name: "" (empty string) - should be
 *    rejected c. enabled: null (invalid type  expects boolean) d. enabled:
 *    "yes" (string instead of boolean)
 * 4. For each case, verify that the update is rejected and the API returns a
 *    validation error (use TestValidator.error)
 * 5. Finally, attempt a valid update and verify it succeeds for control
 */
export async function test_api_aiMallBackend_test_update_section_with_invalid_data(
  connection: api.IConnection,
) {
  // 1. Create a channel with valid properties
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(5),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // 2. Create a section under the channel
  const section = await api.functional.aiMallBackend.sections.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        code: RandomGenerator.alphaNumeric(6),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      } satisfies IAiMallBackendSections.ICreate,
    },
  );
  typia.assert(section);

  // 3a. Attempt to update with an empty code (should fail validation)
  TestValidator.error("reject empty string for code")(() =>
    api.functional.aiMallBackend.sections.update(connection, {
      id: section.id,
      body: { code: "" } satisfies IAiMallBackendSections.IUpdate,
    }),
  );

  // 3b. Attempt to update with an empty name (should fail validation)
  TestValidator.error("reject empty string for name")(() =>
    api.functional.aiMallBackend.sections.update(connection, {
      id: section.id,
      body: { name: "" } satisfies IAiMallBackendSections.IUpdate,
    }),
  );

  // 3c. Attempt to update with enabled: null (should fail validation - non-nullable boolean)
  TestValidator.error("reject null for enabled")(() =>
    api.functional.aiMallBackend.sections.update(connection, {
      id: section.id,
      body: { enabled: null as any } satisfies IAiMallBackendSections.IUpdate,
    }),
  );

  // 3d. Attempt to update with enabled as a string (should fail validation: expects boolean)
  TestValidator.error("reject string for enabled")(() =>
    api.functional.aiMallBackend.sections.update(connection, {
      id: section.id,
      body: { enabled: "yes" as any } satisfies IAiMallBackendSections.IUpdate,
    }),
  );

  // 4. Sanity check: Valid update should work
  const updated = await api.functional.aiMallBackend.sections.update(
    connection,
    {
      id: section.id,
      body: {
        name: RandomGenerator.paragraph()(),
        enabled: false,
      } satisfies IAiMallBackendSections.IUpdate,
    },
  );
  typia.assert(updated);
  TestValidator.equals("section updated")(updated.id)(section.id);
}

