import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendSections } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendSections";

/**
 * Validate prevention of double soft-deletion (archival) for a section entity.
 *
 * This test checks that if an admin attempts to soft-delete an already-archived
 * section, the API rejects the request and returns an error, enforcing
 * retention and non-redundant state transitions. The process simulates a
 * realistic admin flow:
 *
 * 1. Create a business channel (prerequisite for section creation).
 * 2. Create a new section under that channel.
 * 3. Soft-delete the section (initial archival; should succeed).
 * 4. Attempt a second soft-delete on the same section (should fail with error, as
 *    it's already deleted).
 *
 * This ensures the backend logic is robust and compliant with business rules
 * for regulatory archiving.
 */
export async function test_api_aiMallBackend_sections_test_soft_delete_section_already_deleted(
  connection: api.IConnection,
) {
  // 1. Create a new channel (prerequisite for section creation)
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: `Test Channel ${RandomGenerator.alphaNumeric(4)}`,
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // 2. Create a section scoped to the created channel
  const section = await api.functional.aiMallBackend.sections.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        code: RandomGenerator.alphaNumeric(8),
        name: `Test Section ${RandomGenerator.alphaNumeric(4)}`,
        enabled: true,
      } satisfies IAiMallBackendSections.ICreate,
    },
  );
  typia.assert(section);

  // 3. Soft-delete the section (archival should succeed)
  const archivedSection = await api.functional.aiMallBackend.sections.erase(
    connection,
    {
      id: section.id,
    },
  );
  typia.assert(archivedSection);
  TestValidator.predicate("archivedSection.deleted_at should be set")(
    !!archivedSection.deleted_at,
  );

  // 4. Attempt to soft-delete the section again (should throw error)
  await TestValidator.error("second soft-delete must fail")(async () => {
    await api.functional.aiMallBackend.sections.erase(connection, {
      id: section.id,
    });
  });
}

