        tenant_id: tenantId,
        code: channelCode,
        name: `Test Channel ${RandomGenerator.alphabets(6)}`,
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // 2. Create the API gateway for this channel
  const gatewayDomain = `${channelCode}.gateway.example.com`;
