/**
 * Validate that creating an API gateway with a duplicate route_base under the
 * same channel fails.
 *
 * This test ensures that the API enforces the uniqueness of route_base within
 * the same channel. First, create a new channel to act as the container for the
 * gateways. Then, create the first API gateway under this channel with a
 * specific route_base value. Next, attempt to create a second API gateway for
 * the same channel, using the same route_base but a different domain. The
 * request should fail due to the uniqueness constraint on route_base within a
 * channel. Confirm that an error response is produced, indicating the
 * violation.
 *
 * Step-by-step process:
 *
 * 1. Create a new channel (which will be used as channel_id for subsequent API
 *    gateways).
 * 2. Create the first API gateway linked to the new channel with route_base = X
 *    and domain = Y.
 * 3. Attempt to create a second API gateway for the same channel with the same
 *    route_base = X (but domain = Z).
 * 4. Verify that the second API call fails due to the uniqueness constraint on
 *    route_base per channel.
 */
export async function test_api_aiMallBackend_test_create_api_gateway_with_duplicate_route_base_per_channel(
  connection: api.IConnection,
) {
  // 1. Create a new channel.
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: RandomGenerator.name(),
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // 2. Register the first API gateway for this channel, with a chosen route_base and domain.
  const routeBase: string = "/route-" + RandomGenerator.alphaNumeric(6);
  const domain1: string = RandomGenerator.alphaNumeric(8) + ".example.com";
  const gateway1 = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        route_base: routeBase,
        domain: domain1,
        enabled: true,
      } satisfies IAiMallBackendApiGateways.ICreate,
    },
  );
  typia.assert(gateway1);

  // 3. Attempt to create a second API gateway for the same channel with the same route_base, but a different domain.
  const domain2: string = RandomGenerator.alphaNumeric(8) + ".example.org";
  await TestValidator.error(
    "should reject duplicate route_base in the same channel",
  )(async () => {
    await api.functional.aiMallBackend.apiGateways.create(connection, {
      body: {
        channel_id: channel.id,
        route_base: routeBase, // duplicate route_base for same channel
        domain: domain2, // different domain
        enabled: true,
      } satisfies IAiMallBackendApiGateways.ICreate,
    });
  });
}

