/**
 * Test retrieving an API gateway record by its existing id.
 *
 * This test verifies that a created API gateway can be accurately fetched by
 * id, returning all required schema fields and validating that values match
 * those used at creation time.
 *
 * Business context:
 *
 * - Channels represent distinct business units (multi-tenant platform).
 * - API gateways are associated with a channel, governing routing and
 *   integration.
 *
 * Workflow:
 *
 * 1. Create a new channel with realistic values.
 * 2. Create an API gateway for that channel.
 * 3. Fetch the API gateway by its id using the GET endpoint.
 * 4. Assert that the retrieved gateway matches the one created, field-by-field.
 * 5. Check the presence/type of all schema fields, including timestamps and
 *    soft-delete status.
 */
export async function test_api_aiMallBackend_test_get_api_gateway_by_existing_id(
  connection: api.IConnection,
) {
  // 1. Create a new channel (for association)
  const channelInput = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(),
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.paragraph()(2),
    enabled: true,
  } satisfies IAiMallBackendChannels.ICreate;
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: channelInput,
    },
  );
  typia.assert(channel);

  // 2. Create a new API gateway associated with this channel
  const gatewayInput = {
    channel_id: channel.id,
    route_base: "/api/" + RandomGenerator.alphaNumeric(6),
    domain: RandomGenerator.alphaNumeric(10) + ".com",
    enabled: true,
  } satisfies IAiMallBackendApiGateways.ICreate;
  const gateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: gatewayInput,
    },
  );
  typia.assert(gateway);

  // 3. Retrieve the API gateway by its id
  const found = await api.functional.aiMallBackend.apiGateways.at(connection, {
    id: gateway.id,
  });
  typia.assert(found);

  // 4. Validate returned data: all business and schema fields
  TestValidator.equals("gateway id matches")(found.id)(gateway.id);
  TestValidator.equals("channel_id matches")(found.channel_id)(
    gatewayInput.channel_id,
  );
  TestValidator.equals("route_base matches")(found.route_base)(
    gatewayInput.route_base,
  );
  TestValidator.equals("domain matches")(found.domain)(gatewayInput.domain);
  TestValidator.equals("enabled matches")(found.enabled)(gatewayInput.enabled);

  // 5. Verify created_at and updated_at are present and formatted
  TestValidator.predicate("created_at is non-empty string")(
    typeof found.created_at === "string" && found.created_at.length > 10,
  );
  TestValidator.predicate("updated_at is non-empty string")(
    typeof found.updated_at === "string" && found.updated_at.length > 10,
  );

  // deleted_at is nullable - if present, it must be a string or null
  if (found.deleted_at !== undefined && found.deleted_at !== null) {
    TestValidator.predicate("deleted_at is string")(
      typeof found.deleted_at === "string" && found.deleted_at.length > 10,
    );
  }
}

