 *
 * This test ensures correct exclusion of soft-deleted records from API gateway
 * queries.
 *
 * Business context:
 *
 * - API gateways are soft-deletable  i.e., deleted by setting deleted_at, not
 *   physical removal.
 * - Retrieving a soft-deleted gateway should yield not-found (404) or gone (410)
 *   response by business rule, to prevent disclosure or access of deactivated
 *   records.
 *
 * Steps:
 *
 * 1. Create a channel (required for associating the gateway).
 * 2. Create an API gateway associated with the newly created channel.
 * 3. Soft-delete (erase) the created gateway by id.
 * 4. Attempt to retrieve (GET) the same gateway by id.
 * 5. Verify that an error (not found or gone) is thrown, i.e., system does not
 *    return the record.
 */
export async function test_api_aiMallBackend_apiGateways_test_get_api_gateway_by_soft_deleted_id(
  connection: api.IConnection,
) {
  // 1. Create a channel to associate with the API gateway
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: RandomGenerator.name(),
        enabled: true,
      } satisfies IAiMallBackendChannels.ICreate,
    },
  );
  typia.assert(channel);

  // 2. Create the API gateway
  const gateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        route_base: "/api/v1" + RandomGenerator.alphaNumeric(4),
        domain: `${RandomGenerator.alphaNumeric(8)}.example.com`,
        enabled: true,
      } satisfies IAiMallBackendApiGateways.ICreate,
    },
  );
  typia.assert(gateway);

  // 3. Soft-delete the API gateway (set deleted_at)
  const erased = await api.functional.aiMallBackend.apiGateways.erase(
    connection,
    { id: gateway.id },
  );
  typia.assert(erased);
  TestValidator.predicate("deleted_at is set after erase")(!!erased.deleted_at);

  // 4. Attempt to GET by id (should error: not found/gone)
  await TestValidator.error("should not find deleted API gateway")(() =>
    api.functional.aiMallBackend.apiGateways.at(connection, { id: gateway.id }),
  );
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendApiGateways } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendApiGateways";

/**
 * Validates updating an API gateway by changing its mutable fields.
 *
 * This test simulates a real-world scenario where an admin updates an API
 * gateway's configuration:
 *
 * - The test first creates a channel (to be referenced by the API gateway)
 * - Then it creates an API gateway record
 * - It then creates a second channel for updating the `channel_id`
 * - Executes an update of the API gateway, modifying `channel_id`, `route_base`,
 *   `domain`, and `enabled`
 * - It verifies that only these fields changetimestamps (especially
 *   `updated_at`) are updated, audit/immutable fields like `created_at` are
 *   unaffected, and no disallowed fields are mutated
 * - Ensures the returned record matches the update and remains schema-valid
 *
 * Steps:
 *
 * 1. Create channel A (original)
 * 2. Create API gateway with channel A
 * 3. Create channel B (to update to)
 * 4. Update API gateway (set channel_id to channel B, and other fields)
 * 5. Assert updates are reflected (compare all updatable fields), and `updated_at`
 *    changed (is after previous), `created_at` is preserved
 * 6. Assert audit and immutable fields were not erroneously modified
 */
export async function test_api_aiMallBackend_apiGateways_test_update_api_gateway_with_valid_data(
  connection: api.IConnection,
) {
  // Step 1: Create original channel A
  const channelA = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      },
    },
  );
  typia.assert(channelA);

  // Step 2: Create API gateway with channel A
  const apiGateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: {
        channel_id: channelA.id,
        route_base: "/api/" + RandomGenerator.alphaNumeric(6).toLowerCase(),
        domain: RandomGenerator.alphaNumeric(10).toLowerCase() + ".test.com",
        enabled: true,
      },
    },
  );
  typia.assert(apiGateway);

  // Step 3: Create channel B for updating channel_id
  const channelB = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      },
    },
  );
  typia.assert(channelB);

  // Step 4: Prepare new update payload
  const updateBody = {
    channel_id: channelB.id,
    route_base: "/api/" + RandomGenerator.alphaNumeric(7).toLowerCase(),
    domain: RandomGenerator.alphaNumeric(10).toLowerCase() + ".dev.test",
    enabled: false,
  } satisfies IAiMallBackendApiGateways.IUpdate;

  // Step 5: Update the API gateway by id
  const updated = await api.functional.aiMallBackend.apiGateways.update(
    connection,
    {
      id: apiGateway.id,
      body: updateBody,
    },
  );
  typia.assert(updated);

  // Step 6: Check that all mutable fields were updated
  TestValidator.equals("channel_id updated")(updated.channel_id)(
    updateBody.channel_id,
  );
  TestValidator.equals("route_base updated")(updated.route_base)(
    updateBody.route_base,
  );
  TestValidator.equals("domain updated")(updated.domain)(updateBody.domain);
  TestValidator.equals("enabled updated")(updated.enabled)(updateBody.enabled);

  // Step 7: Ensure immutable fields are preserved
  TestValidator.equals("id preserved")(updated.id)(apiGateway.id);
  TestValidator.equals("created_at preserved")(updated.created_at)(
    apiGateway.created_at,
  );

  // Step 8: Confirm updated_at was refreshed (updated is after old)
  TestValidator.predicate("updated_at is more recent")(
    new Date(updated.updated_at).getTime() >
      new Date(apiGateway.updated_at).getTime(),
  );

  // Step 9: deleted_at is unaffected (null or same)
  TestValidator.equals("deleted_at unchanged")(updated.deleted_at)(
    apiGateway.deleted_at,
  );
}

