import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendApiGateways } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendApiGateways";

/**
 * Validate API does not allow updating a soft-deleted API gateway record.
 *
 * This test verifies the soft-deletion enforcement logic: after a gateway is
 * soft-deleted, update requests for that gateway should be rejected (404 Not
 * Found or 410 Gone).
 *
 * Test workflow:
 *
 * 1. Create a channel (channel A) for use in gateway association
 * 2. Create an API gateway associated with channel A
 * 3. Soft-delete (erase) the gateway
 * 4. Attempt to update the soft-deleted gateway
 *
 *    - Should result in an error (404/410), confirming soft-deletion is enforced
 */
export async function test_api_aiMallBackend_apiGateways_test_update_api_gateway_soft_deleted_record(
  connection: api.IConnection,
) {
  // 1. Create a channel (A)
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: "Channel A - " + RandomGenerator.paragraph()(1),
        enabled: true,
      },
    },
  );
  typia.assert(channel);

  // 2. Create an API gateway associated with channel A
  const gateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        route_base: "/api/v1/a/" + RandomGenerator.alphaNumeric(4),
        domain: RandomGenerator.alphaNumeric(7) + ".softdeltest.local",
        enabled: true,
      },
    },
  );
  typia.assert(gateway);

  // 3. Soft-delete (erase) the gateway
  const erased = await api.functional.aiMallBackend.apiGateways.erase(
    connection,
    { id: gateway.id },
  );
  typia.assert(erased);
  TestValidator.predicate("gateway is soft-deleted")(!!erased.deleted_at);

  // 4. Attempt to update the soft-deleted gateway
  const updateProps = {
    channel_id: channel.id,
    route_base: "/api/v1/newroute/" + RandomGenerator.alphaNumeric(4),
    domain: RandomGenerator.alphaNumeric(7) + ".failupdate.local",
    enabled: false,
  };
  await TestValidator.error("update on soft-deleted should fail")(async () => {
    await api.functional.aiMallBackend.apiGateways.update(connection, {
      id: gateway.id,
      body: updateProps,
    });
  });
}

import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendApiGateways } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendApiGateways";

/**
 * Test successful soft deletion (logical removal) of an API gateway record by
 * its id.
 *
 * Validates that after issuing a DELETE request to the endpoint, the
 * `deleted_at` field is set in the returned gateway object, indicating soft
 * deletion (not physical removal). Ensures audit trail compliance and checks
 * that the record persists (by id), but is logically marked deleted. Due to
 * lack of listing/query endpoints in available SDK, listing-based checks are
 * omitted.
 *
 * Steps:
 *
 * 1. Create a channel (channel must exist to create a gateway).
 * 2. Create a new API gateway attached to the channel.
 * 3. Soft delete the API gateway by id using the erase endpoint.
 * 4. Assert the returned gateway object has a non-null, ISO 8601 `deleted_at`
 *    timestamp.
 * 5. Confirm the record remains present and audit fields update correctly.
 * 6. (Optional): Further querying/listing checks skipped (SDK unavailable).
 */
export async function test_api_aiMallBackend_apiGateways_test_soft_delete_api_gateway_success(
  connection: api.IConnection,
) {
  // 1. Create a new channel (as API gateways must be attached to a channel)
  const channelBody = {
    tenant_id: typia.random<string & tags.Format<"uuid">>(),
    code: RandomGenerator.alphaNumeric(8),
    name: RandomGenerator.paragraph()(1),
    enabled: true,
  } satisfies IAiMallBackendChannels.ICreate;
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    { body: channelBody },
  );
  typia.assert(channel);

  // 2. Create an API gateway associated with the channel
  const gatewayBody = {
    channel_id: channel.id,
    route_base: "/api/v1/test" + RandomGenerator.alphaNumeric(4),
    domain: "test-" + RandomGenerator.alphaNumeric(8) + ".example.com",
    enabled: true,
  } satisfies IAiMallBackendApiGateways.ICreate;
  const gateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    { body: gatewayBody },
  );
  typia.assert(gateway);
  TestValidator.equals("gateway should not have deleted_at before erase")(
    gateway.deleted_at,
  )(null);

  // 3. Soft delete the gateway
  const erased = await api.functional.aiMallBackend.apiGateways.erase(
    connection,
    { id: gateway.id },
  );
  typia.assert(erased);

  // 4. Assert that deleted_at is set after erase
  TestValidator.predicate("deleted_at is now set")(
    typeof erased.deleted_at === "string" && !!Date.parse(erased.deleted_at),
  );

  // 5. ID and audit fields remain consistent
  TestValidator.equals("id should remain the same")(erased.id)(gateway.id);
  TestValidator.equals("channel_id remains unchanged")(erased.channel_id)(
    gateway.channel_id,
  );
  TestValidator.predicate("updated_at is a valid timestamp")(
    typeof erased.updated_at === "string" && !!Date.parse(erased.updated_at),
  );
  TestValidator.predicate("created_at remains a valid timestamp")(
    typeof erased.created_at === "string" && !!Date.parse(erased.created_at),
  );

  // 6. Listing/query checks omitted (no function available)
}

