import { ArrayUtil, RandomGenerator, TestValidator } from "@nestia/e2e";
import { IConnection } from "@nestia/fetcher";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import type { IAiMallBackendChannels } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendChannels";
import type { IAiMallBackendApiGateways } from "@ORGANIZATION/PROJECT-api/lib/structures/IAiMallBackendApiGateways";

/**
 * Test soft-delete logic for API Gateway when already deleted.
 *
 * This test ensures that if an admin tries to soft delete (deactivate) an API
 * gateway record which has already been soft deleted (deleted_at is set), the
 * system throws the appropriate error response.
 *
 * Steps:
 *
 * 1. Create a new channel (dependency for the API gateway).
 * 2. Create a new API gateway linked to the channel.
 * 3. Soft delete the API gateway by id (should succeed the first time).
 * 4. Attempt to soft delete the same API gateway again (should return an error
 *    response, as per business logic - already deleted or not found)
 * 5. Verify error is thrown and contains correct type (error occurs)
 */
export async function test_api_apiMallBackend_test_soft_delete_api_gateway_already_deleted(
  connection: api.IConnection,
) {
  // 1. Create a new channel
  const channel = await api.functional.aiMallBackend.channels.create(
    connection,
    {
      body: {
        tenant_id: typia.random<string & tags.Format<"uuid">>(),
        code: RandomGenerator.alphaNumeric(8),
        name: RandomGenerator.paragraph()(1),
        enabled: true,
      },
    },
  );
  typia.assert(channel);

  // 2. Create a new API gateway for that channel
  const gateway = await api.functional.aiMallBackend.apiGateways.create(
    connection,
    {
      body: {
        channel_id: channel.id,
        route_base: "/api/v1/" + RandomGenerator.alphaNumeric(5),
        domain: RandomGenerator.alphaNumeric(8) + ".test.local",
        enabled: true,
      },
    },
  );
  typia.assert(gateway);

  // 3. Soft-delete once (should succeed)
  const softDeleted = await api.functional.aiMallBackend.apiGateways.erase(
    connection,
    {
      id: gateway.id,
    },
  );
  typia.assert(softDeleted);
  if (!softDeleted.deleted_at)
    throw new Error("deleted_at field should be set after soft deletion");

  // 4. Try to soft delete again (should error)
  await TestValidator.error("Double soft delete returns error")(async () => {
    await api.functional.aiMallBackend.apiGateways.erase(connection, {
      id: gateway.id,
    });
  });
}

