  // 3. If there are real records, test filter for moderation_user_id & paginate
  if (allPage.data.length > 0) {
    const some: IReviewModeration = allPage.data[0];
    // (A) Filter by moderator_user_id
    const byModerator = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { moderator_user_id: some.moderator_user_id } },
    );
