    const byAction = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      { body: { action_type: some.action_type } },
    );
    typia.assert(byAction);
    TestValidator.equals("filtered by action_type")(
      true,
    )(byAction.data.every((m) => m.action_type === some.action_type));
    // (C) Date range: To include all
    const allByDateRange = await api.functional.communicationReview.reviewModerations.patch(
      connection,
      {
        body: {
          created_at_range: {
            from: new Date(0).toISOString(),
            to: new Date().toISOString(),
          },
        },
      },
    );
    typia.assert(allByDateRange);
    TestValidator.equals("all in full date range")(
