 * 2. Create a new order_status event with valid status and the real order_id, and confirm a successful response and valid structure.
 * 3. Attempt to create an order_status event with a non-existent order_id, and verify a foreign key violation or not-found error is returned.
 * 4. Attempt to create an order_status event missing required fields to ensure server enforces schema validation and returns a 400 error.
 */
export async function test_api_order_status_create_valid_and_invalid_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid order to use as parent for order_status
  const order: ICartOrderOrder = await api.functional.cartOrder.order.post(
    connection,
    {
      body: {
        user_id: typia.random<string & typia.tags.Format<"uuid">>(),
        cart_id: typia.random<string & typia.tags.Format<"uuid">>(),
      } satisfies ICartOrderOrder.ICreate,
    },
  );
  typia.assert(order);

  // 2. Create valid order_status using the order from step 1
  const now = new Date().toISOString();
  const orderStatus: IOrderStatus = await api.functional.cartOrder.orderStatus.post(
    connection,
    {
      body: {
        order_id: order.id,
        status_code: "pending",
        changed_at: now,
        changed_by: null,
      } satisfies IOrderStatus.ICreate,
    },
  );
  typia.assert(orderStatus);
  TestValidator.equals("order_id")(order.id)(orderStatus.order_id);
  TestValidator.equals("status_code")("pending")(orderStatus.status_code);

  // 3. Try to create order_status with non-existent order_id (should fail)
  await TestValidator.error("foreign key error")(() =>
    api.functional.cartOrder.orderStatus.post(
      connection,
      {
        body: {
          order_id: typia.random<string & typia.tags.Format<"uuid">>(), // Random UUID not linked to actual order
          status_code: "shipped",
          changed_at: new Date().toISOString(),
          changed_by: null,
        } satisfies IOrderStatus.ICreate,
      },
    ),
  );

  // 4. Try to create order_status with missing required fields (should fail)
  await TestValidator.error("schema validation error")(() =>
    api.functional.cartOrder.orderStatus.post(
      connection,
      {
        body: {
          // order_id is missing
          // status_code is missing
          changed_at: new Date().toISOString(),
        } as any, // Bypass TypeScript because we are purposely sending an invalid/partial body
      },
    ),
  );
}

import { TestValidator } from "@nestia/e2e";
