  await TestValidator.httpError("invalid status_code")([400, 422])(() =>
    api.functional.cartOrder.orderStatus.putById(connection, {
      id: created.id,
      body: {
        status_code: "INVALID_STATE",
      } satisfies IOrderStatus.IUpdate,
    }),
  );
}

