import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderLog";
import { IPageIOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderLog";

/**
 * Retrieve a paginated, filterable list of order logs.
 *
 * This test validates advanced filtering and pagination, ensuring the order log retrieval
 * endpoint supports both typical and edge-case queries.
 *
 * Steps:
 * 1. Create multiple order_log entries with varying order_id and event_type.
 * 2. Retrieve order logs by specific order_id (should return matching entries).
 * 3. Retrieve order logs by specific event_type (should return matching entries possibly across different orders).
 * 4. Use pagination to fetch a limited number of results per page (test limit and page params).
 * 5. Attempt to filter using invalid parameters (e.g., non-existent order_id, event_type that doesn't exist) and verify error or empty results.
 * 6. Attempt to use invalid pagination (e.g., negative page, limit over max) and verify safe error/response handling.
 */
export async function test_api_order_log_list_filter_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create multiple order_log entries
  const orderIds: string[] = [
    typia.random<string>(),
    typia.random<string>(),
  ];
  // Map: event_types for diverse event_type coverage
  const eventTypes = ["create", "status_change", "update", "attach_evidence"];
  // Store created logs for targeted queries
  const createdLogs: IOrderLog[] = [];
  for (const order_id of orderIds) {
    for (const event_type of eventTypes) {
      const payload = {
        order_id,
        event_type,
        event_time: new Date(Date.now() - Math.floor(Math.random()*10000)).toISOString(),
      } satisfies IOrderLog.ICreate;
      const log = await api.functional.cartOrder.orderLog.post(connection, { body: payload });
      typia.assert(log);
      createdLogs.push(log);
    }
  }

  // 2. Retrieve logs by specific order_id
  const filterByOrderId: IPageIOrderLog = await api.functional.cartOrder.orderLog.patch(connection, {
    body: { order_id: orderIds[0] } satisfies IOrderLog.IRequest,
  });
  typia.assert(filterByOrderId);
  for (const log of filterByOrderId.data) {
    TestValidator.equals("filter by order_id: matched order_id")(orderIds[0])(log.order_id);
  }

  // 3. Retrieve logs by event_type
  const filterByEventType: IPageIOrderLog = await api.functional.cartOrder.orderLog.patch(connection, {
    body: { event_type: eventTypes[1] } satisfies IOrderLog.IRequest,
  });
  typia.assert(filterByEventType);
  for (const log of filterByEventType.data) {
    TestValidator.equals("filter by event_type: matched type")(eventTypes[1])(log.event_type);
  }

  // 4. Pagination - fetch with limit 2 per page
  const paginated: IPageIOrderLog = await api.functional.cartOrder.orderLog.patch(connection, {
    body: { limit: 2, page: 1 } satisfies IOrderLog.IRequest,
  });
  typia.assert(paginated);
  TestValidator.equals("limit == page size")(2)(paginated.data.length);
  TestValidator.equals("pagination current page")(1)(paginated.pagination.current);

  // 5. Invalid filters - non-existent order_id and event_type expected to return empty
  const invalidFilters: IPageIOrderLog = await api.functional.cartOrder.orderLog.patch(connection, {
    body: { order_id: "NOT-A-REAL-UUID", event_type: "not_an_event" } satisfies IOrderLog.IRequest,
  });
  typia.assert(invalidFilters);
  TestValidator.equals("invalid filters yield empty")(0)(invalidFilters.data.length);

  // 6. Invalid pagination - negative page, over-limit: expect error/robust empty or error
  await TestValidator.httpError("negative page")(400)(() =>
    api.functional.cartOrder.orderLog.patch(connection, {
      body: { page: -1 } satisfies IOrderLog.IRequest,
    })
  );
  await TestValidator.httpError("over-limit page size")(400)(() =>
    api.functional.cartOrder.orderLog.patch(connection, {
      body: { limit: 1000 } satisfies IOrderLog.IRequest,
    })
  );
}

