import { TestValidator } from "@nestia/e2e";
import typia, { tags } from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrderLog } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderLog";

/**
 * Test updating metadata for an existing order_log event.
 *
 * - Confirm that updating mutable fields in an existing log entry works.
 * - Validate audit chain, i.e., changes are saved and retrievable.
 * - Attempt update with an invalid (random) ID and expect not found error (404).
 * - Attempt update with invalid payload and expect input validation error (likely 400).
 *
 * Background: order_log entries are core to the audit/evidence system for CartOrder,
 * used in compliance, forensics, and post-hoc analytics. Edits must be tracked and only
 * permitted fields updated. No silent failures allowed.
 *
 * Steps:
 * 1. Create a valid order_log event via API.
 * 2. Update a mutable field (such as event_description and/or evidence_id).
 * 3. Verify updated fields are reflected in the returned order_log object.
 * 4. Attempt to update with an invalid log ID and assert a 404 is returned.
 * 5. Attempt to update with invalid body (e.g. put 'event_time' as a wrong format) and expect validation failure.
 */
export async function test_api_order_log_update_metadata(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create a valid order_log event
  const createInput: IOrderLog.ICreate = {
    order_id: typia.random<string & tags.Format<"uuid">>(),
    event_type: "create",
    event_time: new Date().toISOString(),
    event_description: "Initial create event.",
    // evidence_id/actor_id can be undefined or random if needed
  };
  const created: IOrderLog = await api.functional.cartOrder.orderLog.post(
    connection,
    { body: createInput },
  );
  typia.assert(created);

  // 2. Update a mutable field (like event_description)
  const updateInput: IOrderLog.IUpdate = {
    event_description: "Revised: auditor note added.",
  };
  const updated: IOrderLog = await api.functional.cartOrder.orderLog.putById(
    connection,
    {
      id: created.id,
      body: updateInput,
    },
  );
  typia.assert(updated);
  TestValidator.equals("event_description changed")(
    updateInput.event_description,
  )(updated.event_description);

  // 3. Changes are saved and audit object is properly updated.
  TestValidator.equals("id remains same")(created.id)(updated.id);
  TestValidator.equals("order_id remains same")(created.order_id)(updated.order_id);
  // event_time is not updated since not provided in the update
  TestValidator.equals("event_time remains")(created.event_time)(updated.event_time);

  // 4. Try updating with invalid ID and expect 404 error
  await TestValidator.httpError("update with unknown id")(404)(() =>
    api.functional.cartOrder.orderLog.putById(connection, {
      id: typia.random<string & tags.Format<"uuid">>(),
      body: updateInput,
    }),
  );

  // 5. Try updating with invalid input: event_time wrong format
  // (Should trigger typia validation error/400)
  const badUpdateInput = {
    event_time: "BAD-TIMESTAMP",
  } as any;
  await TestValidator.httpError("update with invalid payload")(400)(() =>
    api.functional.cartOrder.orderLog.putById(connection, {
      id: created.id,
      body: badUpdateInput,
    }),
  );
}

