import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IOrderEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IOrderEvidence";
import { IPageIOrderEvidence } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIOrderEvidence";

/**
 * Validate paginated and filterable retrieval of order_evidence records.
 *
 * This test ensures that the order_evidence PATCH listing endpoint correctly
 * handles filtering by evidence_type, date range, paging, and error validation
 * for invalid queries.
 *
 * 1. Generate multiple order_evidence records with diverse types and timestamps.
 * 2. Fetch unfiltered, default-paged evidence list.
 * 3. Filter by specific evidence_type and validate the results.
 * 4. Filter by a time window (from_time, to_time), validate inclusion.
 * 5. Combine evidence_type and date range in filter, confirm intersection logic.
 * 6. Test paginated subset (page, limit) and verify metadata + content.
 * 7. Attempt invalid filter input (malformed dates/types/page/limit), ensure error handling.
 */
export async function test_api_order_evidence_filtering_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create diverse evidence records
  const now = new Date();
  const evidences: IOrderEvidence[] = [];
  for (const [type, offsetDays] of [
    ["invoice", 0],
    ["signature", 1],
    ["photo", 2],
    ["receipt", 3],
  ] as const) {
    const evidence = await api.functional.cartOrder.orderEvidence.post(connection, {
      body: {
        order_id: typia.random<string>(), // Replace with real order IDs if needed
        evidence_type: type,
        evidence_uri: `https://evidence.example/${type}/${Date.now()}`,
        provided_at: new Date(now.getTime() - offsetDays * 86400000).toISOString(),
        provided_by: null,
      } satisfies IOrderEvidence.ICreate,
    });
    typia.assert(evidence);
    evidences.push(evidence);
  }

  // 2. Fetch default page (page 1)
  const listDefault: IPageIOrderEvidence = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { page: 1, limit: 2 } satisfies IOrderEvidence.IRequest,
  });
  typia.assert(listDefault);
  TestValidator.equals("pagination: default fetch count")(
    Math.min(2, evidences.length)
  )(listDefault.data.length);

  // 3. Filter by specific evidence_type
  const listInvoice: IPageIOrderEvidence = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { evidence_type: "invoice" } satisfies IOrderEvidence.IRequest,
  });
  typia.assert(listInvoice);
  for (const ev of listInvoice.data) TestValidator.equals("type:invoice")(ev.evidence_type)("invoice");

  // 4. Filter by date range (e.g., only oldest two)
  const oldest = evidences[evidences.length - 1];
  const from = new Date(new Date(oldest.provided_at).getTime() - 1000).toISOString(); // slightly before oldest
  const to = new Date(new Date(evidences[1].provided_at).getTime() + 1000).toISOString(); // just after 2nd
  const listWindow: IPageIOrderEvidence = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { from_time: from, to_time: to } satisfies IOrderEvidence.IRequest,
  });
  typia.assert(listWindow);
  for (const ev of listWindow.data)
    TestValidator.equals("window range")(
      true
    )(
      ev.provided_at >= from && ev.provided_at < to
    );

  // 5. Combine both: 'photo' in a date window
  const photoEv = evidences.find((ev) => ev.evidence_type === "photo");
  if (photoEv) {
    const fromCombo = new Date(new Date(photoEv.provided_at).getTime() - 500).toISOString();
    const toCombo = new Date(new Date(photoEv.provided_at).getTime() + 500).toISOString();
    const listCombo: IPageIOrderEvidence = await api.functional.cartOrder.orderEvidence.patch(connection, {
      body: {
        evidence_type: "photo",
        from_time: fromCombo,
        to_time: toCombo,
      } satisfies IOrderEvidence.IRequest,
    });
    typia.assert(listCombo);
    for (const ev of listCombo.data) {
      TestValidator.equals("combo:photo")("photo")(ev.evidence_type);
      TestValidator.equals("combo:date in window")(
        true
      )(ev.provided_at >= fromCombo && ev.provided_at < toCombo);
    }
  }

  // 6. Test paging (page 2)
  const listPage2: IPageIOrderEvidence = await api.functional.cartOrder.orderEvidence.patch(connection, {
    body: { page: 2, limit: 1 } satisfies IOrderEvidence.IRequest,
  });
  typia.assert(listPage2);
  TestValidator.equals("pagination: page2 count")(1)(listPage2.data.length);
  TestValidator.equals("pagination: page2 meta")(2)(listPage2.pagination.current);

  // 7. Provide invalid filters and check for errors
  await TestValidator.error("invalid date filter")(() =>
    api.functional.cartOrder.orderEvidence.patch(connection, {
      body: { from_time: "NOT-A-DATE" } as any,
    })
  );
  await TestValidator.error("malformed page")(() =>
    api.functional.cartOrder.orderEvidence.patch(connection, {
      body: { page: 0 } as any,
    })
  );
  await TestValidator.error("non-existent type")(() =>
    api.functional.cartOrder.orderEvidence.patch(connection, {
      body: { evidence_type: "nonexistent-type-xyzzy" } as any,
    })
  );
}

