import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";

/**
 * Validate payment listing, search, filter, and pagination for Payment records.
 *
 * This test covers creation of multiple payment records and then verifies listing with various filter and pagination combinations, including valid and invalid scenarios.
 *
 * Background:
 * In a financial system, robust search and pagination of payment records are essential for audit, analytics, and compliance dashboards. Filtering by fields such as date ranges, amounts, payment method, status, or user is critical for operations and user portals. The test also covers consistency of error handling when invalid or unsupported inputs are used for searching.
 *
 * Scenario steps:
 * 1. Create several payment records with distinct attributes (users, payment methods, amounts, dates).
 * 2. List payments with no filter (get all, verify pagination object correctness).
 * 3. Filter payments by known user id, verify only that users payments are returned.
 * 4. Filter payments by payment method id, check results.
 * 5. Filter by amount (exact value or range) and by status.
 * 6. Filter by initiated_at (date range), verify time window restriction.
 * 7. Apply pagination (page/limit), make sure response and record count is correct.
 * 8. Attempt search with invalid user id, malformed parameters, or negative page/limit.
 * 9. Validate consistent error or empty response for unsupported/invalid inputs.
 */
export async function test_api_payment_list_payments_filter_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create several payment records
  const payments: IPayment[] = [];
  for (let i = 0; i < 6; ++i) {
    const uniqueSuffix = `user${i}`;
    const payment: IPayment = await api.functional.paymentsDiscounts.payments.post(
      connection,
      {
        body: {
          transaction_id: crypto.randomUUID(),
          payment_method_id: crypto.randomUUID(),
          amount: 100 + i * 10,
          currency: "KRW",
          description: `Test Payment ${uniqueSuffix}`,
        } satisfies IPayment.ICreate,
      },
    );
    typia.assert(payment);
    payments.push(payment);
  }

  // 2. List all payments (no filter)
  const all: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    { body: {} satisfies IPayment.IRequest },
  );
  typia.assert(all);
  TestValidator.equals("pagination.records")(payments.length)(all.pagination.records); // Should see at least payments.length in DB
  const foundIds = all.data.map((p) => p.id);
  for (const payment of payments) {
    TestValidator.exists(`created payment id ${payment.id}`)(foundIds.includes(payment.id));
  }

  // 3. Filter by payment_method_id
  const paymentMethodFilter: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    { body: { payment_method_id: payments[2].payment_method_id } satisfies IPayment.IRequest },
  );
  typia.assert(paymentMethodFilter);
  for (const payment of paymentMethodFilter.data) {
    TestValidator.equals("payment method match")(payments[2].payment_method_id)(payment.payment_method_id);
  }
  // 4. Filter by amount
  const amountFilter: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    { body: { amount: payments[0].amount } as any }, // The actual API may not support "amount" direct filter unless DTO allows; placeholder for demonstration
  );
  typia.assert(amountFilter);

  // 5. Filter by status
  const status = payments[0].status;
  const statusResult: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    { body: { status } satisfies IPayment.IRequest },
  );
  typia.assert(statusResult);
  for (const payment of statusResult.data) {
    TestValidator.equals("status match")(status)(payment.status);
  }

  // 6. Filter by initiated_at (date window)
  const someInitiated = payments[3].initiated_at;
  const byInitiated: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    {
      body: {
        initiated_from: someInitiated,
        initiated_to: someInitiated,
      } satisfies IPayment.IRequest,
    },
  );
  typia.assert(byInitiated);

  // 7. Pagination (page/limit)
  const pageLimit: IPageIPayment = await api.functional.paymentsDiscounts.payments.patch(
    connection,
    {
      body: {
        page: 1,
        limit: 2,
        sort: "initiated_at:desc",
      } satisfies IPayment.IRequest,
    },
  );
  typia.assert(pageLimit);
  TestValidator.equals("page limit")(2)(pageLimit.data.length);
  TestValidator.equals("pagination.limit")(2)(pageLimit.pagination.limit);

  // 8. Invalid queries: invalid UUID for user_id
  await TestValidator.httpError("invalid user_id UUID")(400)(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, {
      body: { user_id: "invalid-uuid" } satisfies IPayment.IRequest,
    }),
  );
  // Negative page
  await TestValidator.httpError("negative page param")(400)(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, {
      body: { page: -1 } as any,
    }),
  );
  // Negative limit
  await TestValidator.httpError("negative limit param")(400)(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, {
      body: { limit: -10 } as any,
    }),
  );
  // Unsupported filter param
  await TestValidator.httpError("unsupported filter key")(400)(() =>
    api.functional.paymentsDiscounts.payments.patch(connection, {
      body: { unsupported: "zzz" } as any,
    }),
  );
}

