import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPayment } from "@ORGANIZATION/PROJECT-api/lib/structures/IPayment";

/**
 * Update permitted metadata fields of an existing payment record and validate field-level, existence, and data integrity restrictions.
 *
 * This test verifies:
 * 1. Only permitted properties (payment_method_id, completed_at, description) are mutable.
 * 2. Changes to these fields persist and are properly reflected in the response.
 * 3. Attempt to update non-existent payment returns not found error.
 * 4. Invalid update data leads to validation error.
 *
 * Steps:
 * 1. Create a payment record.
 * 2. Update payment_method_id, completed_at, and description via PUT /paymentsDiscounts/payments/{id}.
 * 3. Confirm updated values in the response, validate non-updatable fields remain unchanged.
 * 4. Attempt to update non-existent payment (random UUID), expect 404.
 * 5. Attempt to update with an invalid completed_at value (e.g. not datetime), expect validation error.
 */
export async function test_api_payment_update_metadata_and_error_cases(
  connection: api.IConnection,
): Promise<void> {
  // 1. Create payment
  const createInput: IPayment.ICreate = {
    transaction_id: typia.random<string>(),
    payment_method_id: typia.random<string>(),
    amount: 1000.25,
    currency: "USD",
    description: "Initial payment",
  };
  const payment: IPayment = await api.functional.paymentsDiscounts.payments.post(
    connection,
    { body: createInput },
  );
  typia.assert(payment);

  // 2. Update permitted fields
  const updateInput: IPayment.IUpdate = {
    payment_method_id: typia.random<string>(),
    completed_at: new Date().toISOString(),
    description: "Updated for audit/ref number #4321",
  };
  const updated: IPayment = await api.functional.paymentsDiscounts.payments.putById(
    connection,
    {
      id: payment.id,
      body: updateInput,
    },
  );
  typia.assert(updated);
  TestValidator.equals("payment_method_id updated")(updateInput.payment_method_id)(updated.payment_method_id);
  TestValidator.equals("completed_at updated")(updateInput.completed_at)(updated.completed_at);
  TestValidator.equals("description updated")(updateInput.description)(updated.description);
  TestValidator.equals("id remains")(payment.id)(updated.id);
  TestValidator.equals("amount immutable")(payment.amount)(updated.amount);

  // 3. Attempt to update with non-existent ID
  await TestValidator.httpError("not found")(404)(() =>
    api.functional.paymentsDiscounts.payments.putById(connection, {
      id: typia.random<string>(),
      body: updateInput,
    }),
  );

  // 4. Attempt to update with invalid (malformed) completed_at field
  const invalidUpdate: IPayment.IUpdate = {
    completed_at: "not-a-datetime" as any,
  };
  await TestValidator.error("invalid completed_at format")(() =>
    api.functional.paymentsDiscounts.payments.putById(connection, {
      id: payment.id,
      body: invalidUpdate,
    }),
  );
}

