import { TestValidator } from "@nestia/e2e";
import typia from "typia";

import api from "@ORGANIZATION/PROJECT-api";
import { IPageIPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPageIPaymentMethod";
import { IPaymentMethod } from "@ORGANIZATION/PROJECT-api/lib/structures/IPaymentMethod";

/**
 * Validate paginated and filterable listing of payment methods, including error handling for invalid filters.
 *
 * Scenario:
 * 1. Create multiple payment methods with different types, providers, and activation states for the same user.
 * 2. List all payment methods without filters and ensure all created entries are present.
 * 3. Test filtering by type, provider, and active flag; confirm only relevant records returned each time.
 * 4. Test pagination (limit & page), ensuring correct split & order of items.
 * 5. Test date-range filtering (created_from & created_to).
 * 6. Attempt listing with invalid filter data (unknown type, non-existent user, etc.) and validate proper error/empty set.
 */
export async function test_api_payment_method_list_filter_and_pagination(
  connection: api.IConnection,
): Promise<void> {
  // Prepare same user_id for all records.
  const user_id = typia.random<string>();

  // 1. Create multiple payment methods for the user with varying attributes.
  const paymentMethods: IPaymentMethod[] = [];
  for (const [type, provider, active] of [
    ["VISA", "Stripe", true],
    ["PayPal", "PayPal", true],
    ["Wallet", "Toss", false],
    ["Crypto", "Binance", true],
  ]) {
    const pm: IPaymentMethod = await api.functional.paymentsDiscounts.paymentMethods.post(connection, {
      body: {
        user_id,
        type,
        masked_detail: type + "-XXXX-YYYY",
        provider,
        active,
      } satisfies IPaymentMethod.ICreate,
    });
    typia.assert(pm);
    paymentMethods.push(pm);
  }

  // 2. List all payment methods (no filters), expect all created above.
  const pageAll: IPageIPaymentMethod = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
    },
  });
  typia.assert(pageAll);
  TestValidator.equals("number of methods, unfiltered")(paymentMethods.length)(pageAll.data.length);
  for (const pm of paymentMethods) {
    TestValidator.some("method listed")(pageAll.data.map((d) => d.id))(pm.id);
  }

  // 3. Filter: by type.
  const typeToCheck = "VISA";
  const pageVisa = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      type: typeToCheck,
    },
  });
  typia.assert(pageVisa);
  TestValidator.equals("visa type count")(1)(pageVisa.data.length);
  TestValidator.equals("visa type value")(typeToCheck)(pageVisa.data[0].type);

  // 4. Filter: by provider.
  const providerToCheck = "PayPal";
  const pagePayPal = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      provider: providerToCheck,
    },
  });
  typia.assert(pagePayPal);
  TestValidator.equals("PayPal provider count")(1)(pagePayPal.data.length);
  TestValidator.equals("PayPal provider value")(providerToCheck)(pagePayPal.data[0].provider);

  // 5. Filter: by active=false.
  const pageInactive = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      active: false,
    },
  });
  typia.assert(pageInactive);
  TestValidator.every("all inactive")(pageInactive.data.map((d) => d.active))(false);

  // 6. Pagination: limit and page.
  const pageLimit = 2;
  const page1 = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      limit: pageLimit,
      page: 1,
      sort: "created_at:asc",
    },
  });
  typia.assert(page1);
  TestValidator.equals("page1 size")(pageLimit)(page1.data.length);
  const page2 = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      limit: pageLimit,
      page: 2,
      sort: "created_at:asc",
    },
  });
  typia.assert(page2);
  TestValidator.equals("page2 size")(paymentMethods.length - pageLimit)(page2.data.length);

  // 7. Date range: created_from (set to after first record's created date)
  if (paymentMethods.length > 1) {
    const afterFirstDate = paymentMethods[0].created_at;
    const pageAfterFirst = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
      body: {
        user_id,
        created_from: afterFirstDate,
      },
    });
    typia.assert(pageAfterFirst);
    TestValidator.equals("from date, skips first")(paymentMethods.length - 1)(pageAfterFirst.data.length);
  }

  // 8. Invalid filter: Non-existent type
  const pageBadType = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id,
      type: "NoSuchType",
    },
  });
  typia.assert(pageBadType);
  TestValidator.equals("empty bad type")(0)(pageBadType.data.length);

  // 9. Invalid filter: non-existent user
  const pageBadUser = await api.functional.paymentsDiscounts.paymentMethods.patch(connection, {
    body: {
      user_id: typia.random<string>(),
    },
  });
  typia.assert(pageBadUser);
  TestValidator.equals("empty for missing user")(0)(pageBadUser.data.length);
}

